css:
@-moz-document domain("nguyenkhuyenhcm.edu.vn") {
body
  {
    background: url(https://i.imgur.com/pVHy5Ff.png)center!important;
    background-repeat: no-repeat!important;
    background-attachment: fixed!important;
    background-size: cover!important;
    font-family: "Tahoma"
  }
.card-body, .userprofile .profile_tree section {
    flex: 1 1 auto;
    padding: 1.25rem;
    background-color: rgba(5,5,5,0);
}
.col-1, .col-xs-1, .col-2, .col-xs-2, .col-3, .col-xs-3, .col-4, .col-xs-4, .col-5, .col-xs-5, .col-6, .col-xs-6, .col-7, .col-xs-7, .col-8, .col-xs-8, .col-9, .col-xs-9, .col-10, .col-xs-10, .col-11, .col-xs-11, .col-12, .col-xs-12, .col, .col-auto, .col-sm-1, .col-sm-2, .col-sm-3, .col-sm-4, .col-sm-5, .col-sm-6, .col-sm-7, .col-sm-8, .col-sm-9, .col-sm-10, .col-sm-11, .col-sm-12, .col-sm, .col-sm-auto, .col-md-1, .col-md-2, .col-md-3, .col-md-4, .col-md-5, .col-md-6, .col-md-7, .col-md-8, .col-md-9, .col-md-10, .col-md-11, .col-md-12, .col-md, .col-md-auto, .col-lg-1, .col-lg-2, .col-lg-3, .col-lg-4, .col-lg-5, .col-lg-6, .col-lg-7, .col-lg-8, .col-lg-9, .col-lg-10, .col-lg-11, .col-lg-12, .col-lg, .col-lg-auto, .col-xl-1, .col-xl-2, .col-xl-3, .col-xl-4, .col-xl-5, .col-xl-6, .col-xl-7, .col-xl-8, .col-xl-9, .col-xl-10, .col-xl-11, .col-xl-12, .col-xl, .col-xl-auto
{
	position: relative;
    width: 100%;
    padding-right: 70px;
    padding-left: 70px;
    padding-top: 70px;
    padding-bottom: 70px;
}
.card, #page-enrol-users #filterform, .que .history, .userprofile .profile_tree section, .groupinfobox, .well {
    background-color: rgba(5,5,5,0.65);
    border: 0px solid rgba(0,0,0,0);
    border-radius: .50rem;
}
#region-main {
    border: 0px solid rgba(0,0,0,0);
    border-radius: .50rem;
    background-color: rgba(5,5,5,0.65);
}
.bg-white {
    background-color: rgba(5,5,5,0.0)!important;
    background: linear-gradient(90deg, rgb(5,5,5) 0%, rgba(5,5,5,0.1) 100%);
}
[data-region="drawer"] {
    background-color: rgba(0,0,0,0);
}
.list-group-item {
    position: relative;
    display: block;
    padding: .75rem 1.25rem;
    margin-bottom: -1px;
    background-color: rgba(5,5,5,0.65);
    border: 0px solid rgba(0,0,0,0);
    color: white;
}
a {
    color: white;
    text-decoration: none;
    background-color: transparent;
}
h1, .h1 {
    font-size: 2.5rem;
    color: rgb(255, 155, 0);
}
.breadcrumb {
    display: flex;
    flex-wrap: wrap;
    padding: .75rem 1rem;
    margin-bottom: 1rem;
    list-style: none;
    background-color: black;
    border-radius: 99px;
}
label {
    display: inline-block;
    margin-bottom: .5rem;
    color: rgb(255, 155, 0);
    font-size: 20px;
    font-weight: bold;
}
.form-control {
    display: block;
    width: 100%;
    height: calc(1.5em + .75rem + 2px);
    padding: .375rem .75rem;
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.5;
    color: white;
    background-color: rgba(0,0,0,0);
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
    border: 1px solid #ced4da;
    border-radius: .25rem;
    -webkit-transition: border-color .15s ease-in-out,box-shadow .15s ease-in-out;
    -o-transition: border-color .15s ease-in-out,box-shadow .15s ease-in-out;
    transition: border-color .15s ease-in-out,box-shadow .15s ease-in-out;
}
.btn-secondary, .btn-default {
    color: #fff;
    background-color: rgba(0,0,0,0);
    border-color: rgb(255, 155, 0);
}
.btn-light {
    color: rgb(255, 155, 0);
    background-color: rgba(0,0,0,0);
    border-color: black;
}
.list-group-item.active {
    z-index: 2;
    color: rgb(255, 155, 0);
    background-color: rgba(5,5,5,0.65);
    border-color: rgba(0,0,0,0);
}
th {
    display: table-cell;
    vertical-align: inherit;
    font-weight: bold;
    text-align: -internal-center;
    color: white;
}
.table th
{
    background: blue!important;
}
.d-md-inline
{
    color: rgb(255, 155, 0);
}
h1, h2, h3, h4, h5, h6 {
    margin-top: 0;
    margin-bottom: .5rem;
    color: rgb(255, 155, 0);
}
.path-mod-assign td.submissionstatussubmitted, .path-mod-assign div.submissionstatussubmitted, .path-mod-assign a:link.submissionstatussubmitted {
    color: white;
    background-color: rgba(5,5,5,0.65);
}
.path-mod-assign td.submissionnotgraded, .path-mod-assign div.submissionnotgraded {
    color: white;
    background-color: rgba(5,5,5,0.65);
}
.page-item.active .page-link {
    z-index: 1;
    color: #fff;
    background-color: #128200;
    border-color: #128200;
}
small, .small, #page-admin-search .adminpagepath li {
    font-size: 80%;
    font-weight: 400;
    color: white;
}
.text-truncate {
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
    color: white!important;
}
.dropdown-menu {
    position: absolute;
    top: 100%;
    left: 0;
    z-index: 1000;
    display: none;
    float: left;
    min-width: 10rem;
    padding: .5rem 0;
    margin: .125rem 0 0;
    font-size: 1rem;
    color: white;
    text-align: left;
    list-style: none;
    background-color: rgba(5,5,5,0.65);
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
    border: 1px solid rgba(0,0,0,.15);
    border-radius: .25rem;
}
.dropdown-item.active, .dropdown-item:active {
    color: rgb(255, 155, 0);
    text-decoration: none;
    background-color: rgb(89 89 70 / 0%);
}
.icon-size-3 .icon {
    height: 16px!important;
    width: 16px!important;
    font-size: 16px!important;
    color: white;
}
p {
    margin-top: 0;
    margin-bottom: 1rem;
    color: white;
}
.breadcrumb-item+.breadcrumb-item {
    padding-left: .5rem;
    color: white;
}
.dropdown-item {
    display: block;
    width: 100%;
    padding: .25rem 1.5rem;
    clear: both;
    font-weight: 400;
    color: white;
    text-align: inherit;
    white-space: nowrap;
    background-color: transparent;
    border: 0;
}
.progress-bar {
    display: flex;
    flex-direction: column;
    justify-content: center;
    color: #fff;
    text-align: center;
    white-space: nowrap;
    background: linear-gradient(to right, rgb(255, 155, 0, 0) 0%, rgb(255, 155, 0) 100%)!important;
    -webkit-transition: width .6s ease;
    -o-transition: width .6s ease;
    transition: width .6s ease;
}
.page-link {
    position: relative;
    display: block;
    padding: .5rem .75rem;
    margin-left: -1px;
    line-height: 1.25;
    color: rgb(255, 155, 0);
    background-color: rgba(5,5,5,0.65);
    border: 0px solid #dee2e6;
}
dd, dt
{
    color: white;
}
.calendarwrapper {
    position: relative;
    color:white;
}
.block_online_users .content .info {
    text-align: center;
    color: white;
}
.unlist, .unlist li, .inline-list, .inline-list li, .block .list, .block .list li, .section li.activity, .section li.movehere, .tabtree li {
    list-style: none;
    margin: 0;
    padding: 0;
    color:white;
}
.mt-3, .my-3 {
    margin-top: 1rem!important;
    color:white;
}
.text-muted, a.dimmed, a.dimmed:link, a.dimmed:visited, a.dimmed_text, a.dimmed_text:link, a.dimmed_text:visited, .dimmed_text, .dimmed_text a, .dimmed_text a:link, .dimmed_text a:visited, .usersuspended, .usersuspended a, .usersuspended a:link, .usersuspended a:visited, .dimmed_category, .dimmed_category a, .tag_feed .media .muted a, #page-admin-qtypes .disabled, #page-admin-qbehaviours .disabled, #page-admin-plugins #plugins-control-panel .pluginname .componentname, #page-admin-plugins #plugins-control-panel .version .versionnumber, #page-admin-plugins #plugins-control-panel .notes .requiredby, #page-admin-search .adminpagepath li, .block .minicalendar td.weekend, .section .activity .stealth, .course-content .section-summary .section-summary-activities .activity-count, .course-content ul li.section.hidden .sectionname>span, .course-content ul li.section.hidden .content>div.summary, .course-content ul li.section.hidden .activity .activityinstance, #course-category-listings .listitem[data-visible="0"], #course-category-listings .listitem[data-visible="0"]>div>a, #course-category-listings .listing-pagination-totals.dimmed, .fitem.disabled .fp-btn-choose, .form-defaultinfo, .form-label .form-shortname, .formsettingheading .form-horizontal, .no-felement.fstatic, .path-backup .backup_progress .backup_stage {
    color: white!important;
}
.cal_courses_flt {
    color: white;
    background-color: rgba(5,5,5,0.65);
	border: 1px solid rgb(255 155 0)!important;
}
.custom-select {
    color: white;
    background-color: rgba(5,5,5,0.65)!important;
}
option {
    padding: 0px 2px 1px;
    background-color: rgba(0,0,0,0)
}
nav.navbar .logo img {
    content: url('https://i.imgur.com/A4tOx7e.png');
}
.no-overflow {
    color: white;
}
.forumnodiscuss {
    color:white;
}
.btn-primary {
    background-color: rgba(5,5,5,0.65);
    border-color: rgb(255, 155, 0);
}
.btn-link, .btn.btn-icon, #page-grade-grading-manage .actions .btn-icon.action {
    color: rgb(255, 155, 0);
}
.d-md-inline::after {
    color: rgb(255, 155, 0);
    content: " By Lyy Nyy"
}
.fa-star:before {
    content: "";
    color: rgb(255, 155, 0);
}
.que .formulation {
    color: #FFFFFF;
    background-color: rgba(5,5,5,0.65)!important;
    border-color: none;
}
label {
    color: #FFFFFF;
    font-size: 20px;
}

element.style {
}
.que .info {
    background-color: rgba(5,5,5,0.65)!important;
    border-color: none;
}
.que .info>div {
    color: #FFFFFF;
}
.btn-primary {
    color: rgb(255, 155, 0);
    background-color: rgba(5,5,5,0.65)!important;
    border-color: rgb(255, 155, 0)!important;
}
.btn-secondary, .btn-default {
    color: rgb(255, 155, 0);
    background-color: rgba(5,5,5,0.65)!important;
    border-color: rgb(255, 155, 0)!important;
}
.table, table.collection, table.flexible, .generaltable {
   color: #FFFFFF;
}
.modal-content, .moodle-dialogue-base .moodle-dialogue-wrap.moodle-dialogue-content {
    background-color: rgba(5,5,5,0.65)!important;
  color: #FFFFFF;
}
.que .outcome, .que .comment {
    color: #FFFFFF;
    background-color: rgba(5,5,5,0.65)!important;
    border-color: #FFFFFF;
}
.table th, table.collection th, table.flexible th, .generaltable th, .table td, table.collection td, table.flexible td, .generaltable td {
    color: #FFFFFF;
    background-color: rgba(5,5,5,0.65)!important;
}
}
