css:
@-moz-document domain(dtf.ru) {
  *,
  .comment__arc, 
  .comment__arc::after,
  .comment__status::after,
  .comment__avatar::after,
  .vote--content .vote__button:before,
  .comment__branch:not(.comment__branch--max-level):last-child::before {
      border-radius:0px !important
  }

  .comment__self--author .comment-user__name {
    background-color: #b5e2ff;
    padding: 0 6px;
  }

  .comment__status {
      -webkit-transform: translate(-8px,4px);
      transform: translate(-8px,4px);
  }

  .comment__text__edited {
    font-size: 0px;
    display: inline-block;
    width: 14px;
    height: 14px;
    background-size: contain;
    transform: translateY(-1px);
    background-image: url("data:image/png;base64,")
  }
  .site-header-logo {
background-image: url("data:image/png;base64,");
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center center;
    padding: 0;
    margin: 8px 0 8px 16px;
    width: 60px;
  }
  .site-header-logo>* {
    display: none;
  }
  .content-image {
    background-color: transparent;
  }
  .pswp__button--arrow--left svg {
    margin-left: 2vw;
  }
  .pswp__button--arrow--right svg {
    margin-right: 2vw;
  }
}
