@namespace url(http://www.w3.org/1999/xhtml);
/*
CNET Japan
http://japan.cnet.com/
*/

@-moz-document domain("japan.cnet.com") {
/* template v4 2008-05-08 */
* {
background-image : none !important;
float : none !important;
text-align : left !important;
letter-spacing : 1px !important;
line-height : 1.5 !important;
min-width : 0 !important;
text-indent : 0 !important;
white-space : normal !important;
list-style-image : none !important;
}
pre {
white-space : pre !important;
overflow : auto;
}
html {
font-size : 100% !important;
background-color : #e4e2d4 !important;
}
body {
font-size : 112.5% !important;
margin : 0 auto !important;
padding : 0 !important;
background-color : #e4e2d4 !important;
color : #000 !important;
width : 40em !important;
max-width : 100% !important;
border : 0 !important;
}
table[width]
, tbody[width]
, thead[width]
, tr[width]
, td[width]
{ width : auto !important; }
noscript
, iframe
, hr
, br[clear="all"]
, #footer
, #banner
, .footer
, .banner
, img[width="1"]
, img[width="0"]
, img[src^="http://ad"]
, *[id^="ad"]
, *[class^="ad"]
, *[id*="calendar"]
, *[class*="calendar"]
, *[id*="track"]
, *[class*="track"]
{ display : none !important; }
u { text-decoration : none !important; }
a { text-decoration : underline !important; }
font[size] { font-size : 100% !important; }
table[width] { max-width : 40em !important; }
/* /template */

/* destroy div layout */
#ad_elu_hori
, #ad_elu_hori div
, .site_frame
, .site_frame div {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
border : 0 !important;
margin : 0 !important;
padding : 0 !important;
}
#ad_elu_hori *
, .site_frame * {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
color : #000 !important;
}
#ad_elu_hori, .site_frame a:link { color : #0000ee !important; }
#ad_elu_hori, .site_frame a:visited { color : #551a8b !important; }
#ad_elu_hori, .site_frame a:hover { color : #ee0000 !important; }
#ad_elu_hori, .site_frame a:hover { background-color : transparent !important; }
/* /destroy div layout */

.block_function
, .clbth
, .site_tab
, .navi_category
, .frame_header
, .block_large_top
, .navi_breadcrum
, .tools_wrap
, .block_wp_item
, .frame_col_right
, .site_footer
, .frame_footer
, .block_cvv
, .block_induction_bnet
{ display : none !important; }

.block_zdnet dt.list
{ padding : 0 !important; }

#ad_elu_hori
{ display : block !important; }

.site_logo a:before
{ content : "CNET Japan"; }

}
