css:
@-moz-document domain("im.dingtalk.com") {
  #root {
    height: 100%;
  }

  #layout-main {
    flex: 0 auto;

    width: 100%;

    height: 100%;
  }

  #header {
    position: fixed;
    right: calc(192px + 1%);
    top: 0;
    width: 480px;
    height: 56px;
    z-index: 1;
    transform: translateY(-6px);
    background-image: none;
    background-color: transparent;
    background: none;
    border: none;
  }
  .search-bar-wraper .main-search-2 .select2-search-field input {
    background: transparent;
  }

  #body {
    height: 100%;
  }

  .main-chat {
    background: none !important;
  }

  .chat-head .conv-title .desc {
    text-overflow: unset;
    overflow: auto;
    max-width: 256px;
  }

  .profile-card .detail-content .detail-box {
    background: none !important;
  }

  client-download-guide {
    display: none;
  }
}
