css:
@-moz-document domain("stellarterm.com") {
.Header_nav > .Nav_logo, .Header_nav > .Nav_link.buy_crypto, .Header_nav > .Nav_link[href="/markets/"], .Header_nav > .Nav_link[href="/exchange/"], .Header_nav > .Nav_link[href="/download/"],

.AccountView_content, .so-back.islandBack.islandBack--t > .islandt, .so-back.islandBack > .island > .Generic__content,
.PairPicker_seeOthers, .OfferMakerResultMessage_message, .island__header, .Exchange__warning, #ChartChunk, .island.Orderbook, .so-back.Footer,
.Send_dropdown_block .Send_dropdown .dropdown_list .dropdown_item, .subNavBackClipper + .so-back islandBack islandBack--t,
.AssetCardMain__issuerAccountId
{
    display: none !important;
}

.Send_dropdown_block .Send_dropdown .dropdown_list .dropdown_item:nth-child(3) 
{
    display: block !important;
}

.AssetCardMain {
    border: none;
    background-color: rgb(15, 15, 15) !important;
}

.Header_main .Header_background .Header {
    padding: 2px 10px 2px;
}

.subNav__nav {
    height: 30px;
}

.BalancesTable__head__cell {
    padding: 5px;
}

.BalancesTable__row__item--assetCard {
    padding: 3px;
}

.AssetCardMain__content {
    height: 30px;
}

.AssetCardMain__logo, .AssetCardMain_logo_load {
    width: 40px;
    height: 40px;
}
}

@-moz-document url("https://stellarterm.com/account/") {
.subNavBackClipper + div.so-back.islandBack.islandBack--t,
.so-back.islandBack + .so-back.islandBack, .reserved_description, .reserved_table
{
    display: none !important;
}

#reserved {
    display: block !important;
}

.reserved_Balance .reserved_table_wrapper .reserved_item_total {
  padding: 0;
  font-size: 1.3em;
}

.reserved_Balance {
  padding: 0px 10px;
}
}

@-moz-document url("https://stellarterm.com/account/pending-payments/") {
.Activity_wrap > .Activity_header {
  display: none !important;
}

.AppStretch.AppContainer > .island.Activity {
  padding-top: 0px;
}
}

@-moz-document url("https://stellarterm.com/account/addTrust/") {
.so-back.islandBack.islandBack--t > .island > .island__paddedContent {
  display: none !important;
}

.so-back.islandBack.islandBack--t { 
  padding-top: 0px;
 }
 
}

@-moz-document url-prefix("https://stellarterm.com/account/send") {
.Send_title {
  display: none !important;
}

.so-back.islandBack.islandBack--t { 
  padding-top: 0px;
 }
 
}
