css:
@-moz-document domain("mail.google.com") {
table[role="grid"] {
  /* main list of emails */
  max-width: 1024px;
  margin: 0 auto;
}

tr[role="row"] {
  /* individual email in list */
  background-color: #fdfdfd;
  padding-top: 16px;
  padding-bottom: 16px;
  margin: 8px 0;
  box-shadow: 0 1px 2px rgba(100, 121, 143, 0.12);
}

tr[role="row"].x7 {
  /* individual email, selected */
  background-color: #e8f1fe;
}

div.ata-asE {
  /* new-message indicator at bottom of screen */
    box-shadow: 0 1px 3px rgba(60, 64, 67, 0.3), 0 4px 8px 3px rgba(60, 64, 67, 0.15);
  background-color: #202124;
  border-radius: 4px;
  color: #ffffff;
  left: calc(50% - 50px);
  right: unset;
  padding: 8px;
}

div.ata-asE [role="link"] {
  /* links in new-message indicator */
  color: #8ab4f8;
}

}
