css:
@-moz-document url-prefix("https://vault.bitwarden.com") {
/* Login page */

/* Backgrounds */
html, body{background-color: #111 !important;}

/* Colors */
.card.border-danger p, div.callout-success p, div.callout-warning p, h2.modal-title, .ng-pristine.ng-invalid.ng-touched p, p + label[for="masterPassword"], .form-group label[for="masterPassword"], .swal2-title, label[for="email"], label[for="rememberEmail"]{
    color: #000 !important;
}
.lead.text-center.mx-4.mb-4, .page-header, .secondary-header, .no-items.ng-star-inserted, label, form p, .col-9 p, h2, tr.ng-star-inserted, .col-5 p.text-center.mb-4 + p{color: #fff !important;}

}
