css:
@-moz-document url-prefix('https://www.beyondgaming.be/')
{
    html
  {
    color: #ffffff;
  }

  div a img
  {
    display: none;
  }

  .p-body
  {
    background-color: #181818;
  }

  .block-header
  {
    background: #273b57;
    color: #ffffff;
    border-bottom: 1px solid #273b57;
  }

  .block-container
  {
    background: #282828;
    color: #ffffff;
  }

  .p-header
  {
    background: #0f1d33;
  }

  nav.p-nav
  {
    background: #0f1d33;
  }

  .p-sectionLinks
  {
    background: #273b57;
  }

  a.p-navEl-link.p-navEl-link--splitMenu
  {
    color: #ffffff;
  }

  a.p-navEl-link
  {
    color: #ffffff;
  }

  h2 div a
  {
    color: #ffffff;
  }

  .p-nav-list .p-navEl.is-selected
  {
    background: #273b57;
  }

  a.button--cta.button.button--icon.button--icon--write
  {
    background-color: #273b57;
  }

  .button.button--cta, a.button.button--cta
  {
    border-color: #273b57 #273b57 #273b57 #273b57;
  }

  span.badge.badge--highlighted
  {
    background-color: #2b7177;
  }

  a.button.button--icon.button--icon--bolt
  {
    background-color: #273b57;
  }

  span.button-text
  {
    color: #ffffff;
  }

  a.button.button--icon.button--icon--bolt
  {
    border-color: #273b57;
  }

  .p-navgroup
  {
    background: #273b57;
  }

  a.p-navgroup-link.p-navgroup-link--iconic.p-navgroup-link--user
  {
    background-color: #273b57;
  }

  a.p-navgroup-link.p-navgroup-link--iconic.p-navgroup-link--conversations.js-badge--conversations.badgeContainer
  {
    background-color: #273b57;
  }

  a.p-navgroup-link.p-navgroup-link--iconic.p-navgroup-link--alerts.js-badge--alerts.badgeContainer
  {
    background-color: #273b57;
  }

  a.p-navgroup-link.p-navgroup-link--iconic.p-navgroup-link--search
  {
    background-color: #273b57;
  }

  .p-navgroup-link
  {
    border-left: 1px solid #273b57;
  }

  .p-nav
  {
    color: #ffffff;
  }

  .p-navgroup-link.badgeContainer
  {
    opacity: 1;
  }

  .node-icon i::before
  {
    color: #3a3a3a;
  }

  .node--unread .node-icon i::before
  {
    color: #2b7177;
  }

  .subNodeLink.subNodeLink--unread::before, .subNodeLink.subNodeLink--unread .subNodeLink-icon
  {
    color: #2b7177;
  }

  a
  {
    color: #ffffff;
  }

  .block-footer
  {
    background: none;
    border-top: 1px solid #273b57;
  }

  .node + .node
  {
    border-top: 1px solid #3a3a3a;
  }

  .avatar img
  {
    background-color: #0f1d33;
  }

  .block-minorHeader
  {
    color: #ffffff;
  }

  .p-footer
  {
    background: #273b57;
  }

  .p-footer a
  {
    color: #ffffff;
  }

  .subNodeLink::before, .subNodeLink .subNodeLink-icon
  {
    color: #3a3a3a;
  }

  .overlay
  {
    background: #181818;
  }

  .formSubmitRow-bar
  {
    background: none;
  }

  .button.button--primary, a.button.button--primary, a.button.button--primary:hover
  {
    background: #273b57 !important;
    border-color: #273b57;
  }

  .badge.badge--highlighted, .badgeContainer.badgeContainer--highlighted::after
  {
    background: #282828;
  }

  .button.button--primary:not(.button--splitTrigger):focus
  {
    background-color: #273b57;
  }

  .scNormalThreads
  {
    background: #273b57;
    color: #ffffff;
  }

  .structItem
  {
    border-top: 1px solid #3a3a3a;
  }

  .scStickyThreads
  {
    background: #273b57;
    color: #ffffff;
  }

  .block-filterBar
  {
    background: #3a3a3a;
    border-bottom: 1px solid #273b57;
  }

  .pageNav-page.pageNav-page--current
  {
    background: #3a3a3a;
    color: #273b57;
  }

  .pageNav-page
  {
    background: #3a3a3a;
    color: #ffffff;
  }

  .pageNav-jump
  {
    background: #3a3a3a;
    color: #273b57;
  }

  .button.button--link, a.button.button--link
  {
    background: #3a3a3a;
    color: #ffffff;
    border-color: #3a3a3a;
  }

  .message-cell.message-cell--user, .message-cell.message-cell--action, .message-cell.message-cell--main
  {
    background: #3a3a3a;
  }

  .message-articleUserInfo
  {
    background: #3a3a3a;
  }

  .fr-box.fr-basic
  {
    background: #3a3a3a;
  }

  .bbCodeBlock-title
  {
    background: #181818;
  }

  .bbCodeBlock
  {
    background: #3a3a3a;
    border: 1px solid #636d8f;
    border-left-color: rgb(99, 109, 143);
    border-left-style: solid;
    border-left-width: 1px;
    border-left: 3px solid #273b57;
  }

  .structItem.is-unread .structItem-title a:not(.labelLink)::after
  {
    background: #2b7177;
  }
  
    .structItem.is-unread .structItem-title a
  {
    color: #2b7177;
  }

  .node-extra-row
  {
    color: #ffffff;
  }

  .pairs > dt
  {
    color: #ffffff;
  }

  a:hover
  {
    color: #273b57;
  }

  a:link, a:visited, a.active
  {
    color: #ffffff;
  }

  .block-header .block-desc
  {
    color: #ffffff;
  }

  .contentRow-minor
  {
    color: #ffffff;
  }

  .structItem-minor
  {
    color: #ffffff;
  }

  .structItem-minor a
  {
    color: #6a6a6a;
  }

  .label.label--primary
  {
    color: #2b7177;
    border-color: #2b7177;
  }

  .structItem-pageJump a
  {
    background: none;
  }

  .block--messages .message
  {
    color: #ffffff;
  }

  .u-concealed, .u-cloaked, .u-cloaked a, .u-concealed--icon, .u-concealed--icon a
  {
    color: #ffffff !important;
  }

  .u-concealed a
  {
    color: #ffffff;
  }

  .message-newIndicator, a.message-newIndicator
  {
    background: #2b7177;
  }

  .bbWrapper a
  {
    color: #ffffff;
  }

  .tabs--standalone .tabs-tab.is-active
  {
    color: #ffffff;
    border-color: #273b57;
  }

  .tagItem
  {
    background: #273b57;
  }

  .fr-toolbar .fr-command.fr-btn, .fr-popup .fr-command.fr-btn, .fr-modal .fr-command.fr-btn
  {
    color: #273b57;
  }

  .fr-box.fr-basic
  {
    background: #cccccc;
  }

  .button, a.button
  {
    background: #273b57;
    border-color: #273b57;
  }

  .button:hover
  {
    background: #273b57;
  }

  a.button:hover
  {
    background: #273b57;
  }

  #js-XFUniqueId145
  {
    background: #273b57;
  }

  #js-XFUniqueId146
  {
    background: #273b57;
  }

  .p-description
  {
    color: #ffffff;
  }
  .is-unread .structItem-title a:link {
	color: #2b7177;
  }
  .is-unread .structItem-title a:nth-of-type(3) {
	color: #2b7177;
  }
  .pollResult-bar > i {
    background: #2b7177;
}
.pollResult--voted .pollResult-response::before {
    color: #2b7177;
}
.bookmarkLink.bookmarkLink--highlightable.is-bookmarked {
    color: #2b7177;
}
bbTable > table > thead > tr > td, .bbTable > table > tbody > tr > td {
    background: #2b7177;
}
bbTable > table > thead > tr > td > b > span, .bbTable > table > tbody > tr > td > b > span {
    background: #ffffff;
}
.bbCodeBlock--unfurl {
    color: #ffffff;
}
.fr-view blockquote {
    border-left: 3px solid #2b7177;
}
.fr-view blockquote[data-quote]::before {
    color: #2b7177;
}
.block-tabHeader .tabs-tab.is-active {
    border-color: #2b7177;
}
.block-tabHeader {
    background: #2b7177;
}
.menu-content {
    background: #2b7177;
	border-color: #ffffff;
}
.menu-header {
    background: #2b7177;
	color: #ffffff;
}
.menu-footer {
    background: #2b7177;
	border-color: #ffffff;
}
.menu-tabHeader .tabs-tab.is-active {
    border-color: #2b7177;
	    background: #2b7177;
}
.menu-tabHeader {
    background: #2b7177;
}
.menu-row.menu-row--alt {
    background: #2b7177;
}
.p-navEl-splitTrigger::after {
    color: #2b7177;
}
div a img {
    display: inline;
}
}
