css:
@-moz-document url-prefix("https://clients.boursorama.com/") {
/* Backgrounds */
html, body, footer{background-color: #111 !important;}
#dropdown-services, #dropdown-products{background-color: #202020 !important;}

/* Text colors */
#dropdown-services .subnav__list a, #dropdown-products .subnav__list a{color: #fff !important;}
.subnav__header{color: #009de0 !important;}

/* Removing borders */
.c-promotional-cards-wrapper.slick-initialized:after, .c-promotional-cards-wrapper.slick-initialized:before{background: #111 !important;}
}
