css:
@-moz-document domain("vk.com") {
[dir] .flat_button {
 background: url(https://media.discordapp.net/attachments/779228694970892288/891400734673891338/e81331256798278a.png) repeat-x;  
    border-top: 1px solid #517295;
    border-bottom: 1px solid #4e6f93;
    border-left: 1px solid #4e6f93;
    border-right: 1px solid #4e6f93;
    border-radius: 0px;
    margin: 0;
    border: 0;
}

[dir] .FlatButton__in {
     background: url(https://media.discordapp.net/attachments/779228694970892288/891400734673891338/e81331256798278a.png) repeat-x;  
    border-top: 1px solid #517295;
    border-bottom: 1px solid #4e6f93;
    border-left: 1px solid #4e6f93;
    border-right: 1px solid #4e6f93;
    border-radius: 0px;
    margin: 0;
    border: 0;
}

[dir] .post_img {
    border-radius: 0%;
}

.flat_button.ui_load_more_btn {
    color: #ffffff;
}

[dir] .post_field_user_image {
    border-radius: 0%;
}

[dir] .nim-dialog.nim-dialog_classic .nim-dialog--photo .nim-peer .nim-peer--photo>img, [dir] .nim-dialog.nim-dialog_classic .nim-dialog--photo .nim-peer .nim-peer--photo .im_grid>img {
    border-radius: 0%;
}

[dir] .nim-peer.nim-peer_small .nim-peer--photo, [dir] .nim-peer.nim-peer_small .nim-peer--photo .im_grid {
    border-radius: 0%;
}

.FlatButton--secondary {
    color: #ffffff;
}

[dir] .FlatButton--secondary {
    background: url(https://media.discordapp.net/attachments/779228694970892288/891400734673891338/e81331256798278a.png) repeat-x;
}

[dir] .FlatButton--primary {
    background: url(https://media.discordapp.net/attachments/779228694970892288/891400734673891338/e81331256798278a.png) repeat-x;
}

.FlatButton--secondary:not(.disable):hover {
    color: #e2e2e2;
    opacity: 1;
}

.flat_button.secondary, .button_gray button {
    color: #ffffff; 
}

.FlatButton {
    text-shadow: 0 1px 0 #45688e;
}

.flat_button.secondary.button_light, .box_controls .flat_button.secondary {
    color: #ffffff;
}

.flat_button.secondary.button_light, .box_controls .flat_button.secondary {
    color: #ffffff;
}

.page_actions_dd_label, .page_actions_header_inner {
    color: #ffffff;
}

[dir] .page_list_module .thumb {
    border-radius: 0%;
}

[dir] .module_body .people_cell_img {
    border-radius: 0%;
}

[dir] .post_image_stories_unseen:after {
    border-radius: 0%;
}
}
