	Version: 1
*/

html {
	background: #141414;
    color: #eaeaea !important;
}

a, .p-pageWrapper, .newsletter-subscribe, .subscribe-form, #top_menu, #mainNav, .p-nav-inner, .p-sectionLinks, .tabs--standalone, .block-container, .message-cell.message-cell--user, .block--messages .message, .bbCodeBlock, .bbCodeBlock-title, .reactionsBar, .block-header {
	background-color: #5e5e5e !important;
}

.pageNav-jump, .block-filterBar {
	background: linear-gradient(0deg, #5e5e5e, #464646);
}

.p-nav-list .p-navEl.is-selected, .notice {
	background-color: #464646 !important;
}

a, #mainNav ul li a, .p-nav a, .p-sectionLinks, .notice.notice--primary, .block--messages .message {
	color: #cecece !important;
}
}
