css:
@-moz-document domain('forums.d2jsp.org'), url-prefix('http://mediavida.com'),url-prefix('https://forums.d2jsp.org'),url-prefix('http://www.forums.d2jsp.org'),url-prefix('https://forums.d2jsp.org') {

body, input, select, textarea, .inb, .mpl {
    background: #333 !important;
}
.foot, .ftb TH, .ftbt TH, .pg a, .pg s, dd, dt {
    background: #333;
    color:white
}
.bar, .bc1, .pg .bc1, .news, .tzc tr:nth-child(2n+1) {
    background: #444;
    color:white
}
