css:
@-moz-document domain("elearning.uni-bremen.de") {
body {
  color: #c9d1d9 !important;
  background-color: #0d1117 !important;
}
textarea {
  background-color: #0d1117 !important;
  color: white !important;
}
::placeholder {
  color: #c9d1d9 !important;
  opacity: 1 !important;
}
:-ms-input-placeholder {
  color: #c9d1d9 !important;
}
::-ms-input-placeholder {
  color: #c9d1d9 !important;
}
select {
  color: #c9d1d9 !important;
  background-color: #0d1117 !important;
}
#barBottomContainer {
  background-color: #0d1117 !important;
  color: #c9d1d9 !important;
}
#layout-sidebar,
#layout_container,
#page_title_container,
#flex-header,
#tabs,
#tabs2,
#layout_page,
#layout_content,
#tabs li,
.smiley-tabs li,
.helpbar {
  background: #0d1117 !important;
}
#page_title_container,
#tabs {
  border-bottom: 0px !important;
  border-top: 0px !important;
  border: 0px !important;
}
#layout_wrapper #layout_page .tabs_wrapper {
  background-color: #0d1117 !important;
}
table.default > caption,
#tabs li.current,
#tabs li.current a,
#tabs li.current span.quiet,
.smiley-tabs li.current a,
.smiley-tabs li.current span.quiet,
body:not(.fixed) #barTopMenu a {
  color: #c9d1d9 !important;
}
#tabs a,
#tabs span.quiet,
.smiley-tabs a {
  color: #c9d1d9 !important;
}
tr.sortable,
table.default > thead > tr > th {
  background-color: #21262d !important;
  border: 0px !important;
}
a,
a:link,
a:visited {
  color: #c9d1d9 !important;
}
/*Link generell*/
a:hover,
a:active {
  color: white !important;
}
/*Link mouseover*/
article.studip header h1,
section.contentbox header h1 {
  color: white !important;
}
.helpbar {
  right: 0px !important;
}
.studip-widget .widget-header {
  background-color: #0d1117 !important;
  color: #c9d1d9 !important;
  font-weight: normal !important;
}
.studip-widget {
  border: 1px solid #0d1117 !important;
  transition: none !important;
}
/*DRAG N DROP*/
.js .drag-and-drop {
  background-color: #21262d !important;
  color: #c9d1d9 !important;
}
/*NAVBAR*/
li.active {
  background: none !important;
  margin-left: -5px !important;
  -webkit-box-shadow: none !important;
  -moz-box-shadow: none !important;
  box-shadow: none !important;
  position: relative !important;
  margin-right: -5px !important;
}
.ui-dialog.studip-confirmation .ui-dialog-titlebar {
  background-color: #c9d1d9 !important;
  color: #000 !important;
  text-align: left !important;
}
#zurueckDiv,
#adressDiv,
#simpleSearchDiv,
.lectureItem0,
.lectureItem1 {
  border: 0px !important;
}
.lectureItem0,
.lectureItem1 {
  background-color: #21262d !important;
}
abbr {
  color: #c9d1d9 !important;
}
.lectureItemTitle {
  font-weight: bold !important;
}
*:focus,
.focus_box,
a.button:focus,
button.button:focus {
  outline: 0px !important;
}
/*NAVBAR SEARCH*/
input {
  background-color: #0d1117 !important;
  color: #c9d1d9 !important;
}
#globalsearch-searchbar #globalsearch-list {
  background-color: #0d1117 !important;
}
/*NAVBAR USER ICON*/
#notification_marker {
  color: #28497c !important;
  background-color: #0d1117 !important;
}
.action-menu.avatar-menu .action-menu-content {
  background: #0d1117 !important;
  background-color: #0d1117 !important;
}
.action-menu.avatar-menu .action-menu-content div {
  color: #c9d1d9 !important;
}
/*SIDEMENU*/
.sidebar .sidebar-widget-header {
  background: #21262d !important;
  color: #c9d1d9 !important;
  padding-left: 10px !important;
}
.sidebar-widget-content {
  background: #0d1117 !important;
  border-top: 0px !important;
}
.sidebar .sidebar-widget,
.sidebar .sidebar-widget-placeholder {
  border: 1px solid #6c7175 !important;
}
div.messagebox_info {
  color: white !important;
  background-color: #21262d !important;
  border-color: #21262d !important;
}
article.studip,
section.contentbox {
  border-color: #0d1117 !important;
}
article.studip header,
section.contentbox header {
  background-color: #21262d !important;
}
ul#tabs {
  padding-top: 10px !important;
}
/*STUNDENPLAN*/
table#schedule_data thead tr td,
table#schedule_data tbody td:first-child,
div.schedule_hours {
  background-color: #21262d !important;
  color: #c9d1d9 !important;
}
div.schedule_day {
  border-right: 2px double #21262d !important;
}
nav.calendar-nav .calhead,
h1,
h2,
h3,
h4,
h5,
h6 {
  color: #c9d1d9 !important;
  font-weight: normal !important;
}
.studip-tooltip {
  background-image: linear-gradient(to bottom, #21262d, #21262d) !important;
  border: 0px !important;
  -webkit-border-radius: 4px !important;
  -moz-border-radius: 4px !important;
  border-radius: 4px !important;
  -webkit-box-shadow: 0 0px 0px !important;
  -moz-box-shadow: 0 0px 0px !important;
  box-shadow: 0 0px 0px !important;
  text-shadow: 0 0px 0px !important;
}
/*NACHRICHTEN*/
body#messages-overview {
  background-color: #0d1117 !important;
}
.ui-dialog.ui-widget.ui-widget-content {
  border: 0px !important;
  box-shadow: 0 0 15px rgba(0, 0, 0, 0.5) !important;
}
.ui-widget-content {
  background: #0d1117 !important;
  color: #c9d1d9 !important;
}
.message_body {
  background-color: #21262d !important;
  margin: 0px !important;
  margin-top: 5px !important;
  padding: 10px !important;
}
.ui-dialog.ui-widget.ui-widget-content .ui-dialog-buttonpane {
  border-color: #2a3f5b !important;
}
.ui-dialog.ui-widget.ui-widget-content .ui-dialog-buttonpane .ui-button,
a.button,
button.button {
  background: #21262d !important;
  border: 0 !important;
  color: #c9d1d9 !important;
  font-weight: normal !important;
}
article.studip header h1,
section.contentbox header h1 {
  color: #c9d1d9 !important;
}
/*GRUPPEN*/
section.course-statusgroups article section table tfoot tr td,
section.course-statusgroups article section table thead tr th {
  background-color: #21262d !important;
}
/*BLUBBER*/
#blubber_threads > .thread > .contextinfo {
  background-color: #0d1117 !important;
  color: #c9d1d9 !important;
}
#blubber_threads .comments {
  background-color: #0d1117 !important;
  border-top: 1px solid #0d1117 !important;
  background-color: #0d1117 !important;
}
#blubber_threads .comments .posting {
  background-color: #0d1117 !important;
}
/*ANKÃœNDIGUNGEN*/
form.default fieldset > legend {
  background-color: #21262d !important;
  color: #c9d1d9 !important;
}
form.default footer {
  background-color: #21262d !important;
}
div.messagebox_info {
  color: #c9d1d9 !important;
}
/*FORM*/
form.default input[type="date"],
form.default input[type="email"],
form.default input[type="number"],
form.default input[type="password"],
form.default input[type="tel"],
form.default input[type="text"],
form.default input[type="time"],
form.default input[type="url"],
form.default select,
form.default textarea {
  color: #c9d1d9 !important;
}
/*DoIT*/
.public-intro{
  background-color: #1f3f70 !important;
}

.msg-box-info{
  background-color: #2f2f306b !important;
  color: #f6f6f6 !important;
}
.public_thema_title, .thema-title-block, 
.aufgabe-title-block, .student_work_main{
  background-color:#2f2f306b !important;
}
.student_feedback_main{
  background-color: #27732d !important ;
}
/*LOGIN*/
div.index_container div.index_main {
  background-color: #0d1117 !important;
}
/*FOOTER*/
#barBottomright,
#layout_footer {
  color: #c9d1d9 !important;
}
#barBottomright > ul > li > a,
#layout_footer > ul > li > a {
  color: #c9d1d9 !important;
}

/*changes*/
/*login screen and opencast replays*/
.index_main,
.oce_list > li,
.oce_playercontainer {
    background: black !important;
}
.index_main,
.oce_list > li {
    border: 1px solid white;
}
}
