css:
@-moz-document url("https://dota2.ru/") {
.tournament, .streams, .video, .memes, .index__right-bar, .owl-matches.new-m-matches.overflow-list{
    display: none;
}

.forum .forum__item .component-block__img {
    display: block;
    margin: 10px 10px 10px 0;
}

.index__left-bar{
    min-width: 50%;
    max-width: 50%;
    margin: 0;
}

@media (max-width: 1023px){
    .index__left-bar{
        min-width: 100%;
        max-width: 100%;
    }
}

.component-block{
    width: 100%;
}

.forum__item .short{
    margin: 0;
    position: static;
    display: block;
}

.short .topic-header, .short .author, .short .node{
    display: none;
}

.short .message{
    margin-top: 8px;
    padding-left: 66px;
    color: #8F8F91;
}

.index__wrap{
    gap: 0 20px;
}

.index__main{
    margin: 0 0px;
}
}

@-moz-document domain("dota2.ru") {
.navigation__item:nth-child(3), .navigation__item:nth-child(4), .navigation__item:nth-child(5), .navigation__item:nth-child(6), .navigation__item:nth-child(9){
    display: none;
}

.adv{
    display: none !important
}
}
