css:
@-moz-document url-prefix("https://kuliah.ppkia.ac.id/") {

body {
	background-color: rgba(25, 59, 104, 0.1);
}

a {
	text-decoration: none;
}

#topofscroll.main-inner {
	background-color: transparent!important;
	color: #193B68;
}

#topofscroll.main-inner h1 {
	font-size: 32px;
}

#region-main {
	padding: 12px;
	padding-top: 24px;
    border-radius: 12px;
}

#region-main-box a, .navbar.fixed-top a {
    color: #193B68;
    text-decoration: none;
    font-weight: bold;
    transition: 0.15s linear;
}

#region-main-box a:hover, .navbar.fixed-top a:hover {
    color: #1479ff;
}

.navbar-light .navbar-nav .show>.nav-link, .navbar-light .navbar-nav .active>.nav-link, .navbar-light .navbar-nav .nav-link.show, .navbar-light .navbar-nav .nav-link.active {
	color: #193B68;
}

.nav-item {
	display: flex;
    justify-content: center;
    align-items: center;
}

.navbar.fixed-top {
    margin: 12px;
    border-radius: 12px;
    box-shadow: 0 3px 3px 1px rgba(20, 121, 255, 0.1);
/* 	filter: blur(2px); */
    backdrop-filter: blur(2px);
    background-color: rgba(255, 255, 255, 0.66)!important;
}

.navbar-nav.ml-auto .popover-region.collapsed.popover-region-notifications .popover-region-toggle.nav-link.icon-no-margin:hover, .navbar-nav.ml-auto .popover-region.collapsed a:hover {
	color: #1479ff;
    border: 0;
}

.moremenu.navigation.observed, .login-wrapper, .login-container {
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 0.5rem;
    margin: 0;
}

.login-container {
	padding: 1rem 2rem 2rem 2rem!important;
}

.login-instructions {
  margin-top: 7rem;
}

.secondary-navigation.d-print-none {
	padding: 15px 0;
}

.secondary-navigation.d-print-none .moremenu.navigation.observed {
	justify-content: end;
    align-items: center;
    border: 0;
    margin: 0;
    font-weight: bold;
    background-color: #fff;
    border-radius: 0.5rem;
}

.moremenu .nav-tabs {
    background-color: transparent!important;
}

.secondary-navigation .navigation .nav-tabs .nav-link {
	color: #193B68;
	transition: 0.2s ease;
}

.secondary-navigation .navigation .nav-tabs .nav-link:hover {
    color: #1479FF;
    height: calc(100% - 26%);
    border-radius: 0.5rem;
    border: 2px solid #1479FF;
    background-color: #fff;
}

.secondary-navigation.d-print-none .moremenu.navigation.observed .nav.more-nav.nav-tabs li {
    margin: 0 4px;
    background-color: transparent!important;
}

.nav-item.dropdown.dropdownmoremenu .dropdown-toggle.nav-link:hover {
    color: #1479FF!important;
    background-color: transparent!important;
    border-bottom: 2px solid #1479FF!important;
    box-shadow: none;
    border-bottom: 0;
}

a.nav-link.active.active_tree_node {
    height: calc(100% - 26%);
    border-radius: 6px!important;
    transition: 0.2s ease;
}

a.nav-link.active {
    height: calc(100% - 0%);
}

.moremenu .nav-link.active {
    background-color: #1479FF;
    color: #fff!important;
    height: calc(100% - 0%);
    border-radius: 6px;
    box-shadow: 0 3px 3px 1px rgba(20, 121, 255, 0.1);
    border-bottom: 0;
}

.primary-navigation .navigation .nav-link {
    margin: 0 3px;
}

.navbar.fixed-top.navbar-light.bg-white.navbar-expand .nav.more-nav.navbar-nav .nav-link.active {
    height: calc(100% - 26%);
}

.primary-navigation .navigation .nav-link:hover {
    background-color: #fff;
    color: #1479FF!important;
    height: calc(100% - 26%);
    border-radius: 6px;
    box-shadow: 0 3px 3px 1px rgba(20, 121, 255, 0.1);
    border-bottom: 0;
    border: 2px solid #1479FF;
}

.moremenu .nav-link.active:hover {
    border: 2px solid #1479FF;
    color: #1479FF!important;
}

.dropdown-menu.dropdown-menu-right.show a.dropdown-item.pl-5, a.dropdown-item.menu-action {
    margin: 6px;
    width: calc(100% - 7%);
}

.dropdown-menu.dropdown-menu-right.show a.dropdown-item.pl-5:hover, a.dropdown-item.menu-action:hover {
    background-color: #1479FF;
    border: 0;
    color: #fff!important;
    border-radius: 6px;
    box-shadow: 0 3px 3px 1px rgba(20, 121, 255, 0.1);
}

.dropdown-menu.dropdown-menu-right.show {
    margin-top: calc(100% - 95%);
    border: 2px solid #193B68;
    padding: 0;
}

#page-navbar .breadcrumb {
    font-weight: bold;
}

#page-navbar .breadcrumb a:hover {
    color: #133B68;
    text-decoration: none;
}

/* #region-main .form-control.form-control-lg  {
    z-index: 123;
} */

.login-container, .form-control, .custom-select.urlselect.text-truncate.w-100 {
    color: #193B68;
    border: 2px solid #193B68;
    box-shadow: 0 3px 3px 1px rgba(20, 121, 255, 0.1);
    font-weight: bold;
}

 .custom-select.urlselect.text-truncate.w-100 option {
    background-color: rgba(255, 255, 255, 0.66)!important;
}

.input-group>.input-group-append>.btn {
    background-color: #1479FF;
    box-shadow: 0 3px 3px 1px rgba(20, 121, 255, 0.1);
}

.btn-primary {
    color: #fff;
    background-color: #1479FF;
    border: 2px solid #1479FF;
    box-shadow: 0 3px 3px 1px rgba(20, 121, 255, 0.1);
    font-weight: bold;
    transition: 0.2s ease;
}

.btn-primary:hover {
    color: #1479FF;
    background-color: #fff;
}

.btn.btn-primary.search-icon {
    border: 0;
    box-shadow: 0 3px 3px 1px rgba(20, 121, 255, 0.1);
}

.btn.btn-primary.search-icon:hover {
    color: #1479FF;
    background-color: #fff;
    border: 1px solid #1479FF;
}

body.pagelayout-login #page {
    background-image: none;
}

.login-divider {
    display: block;
}

.login-container {
    padding: 2rem;
}

.login-container .login-divider {
    display: none;
}

.login-form-submit.form-group {
    position: absolute;
}

.login-form-forgotpassword.form-group a {
    float: right;
}

.loginform .d-flex {
    display: none!important;
}

.login-container .login-logo {
    margin-right: 2rem;
    margin-bottom: 0;
}

.loginform .alert.alert-danger {
    position: absolute;
    top: 1px;
    left: 0px;
    width: 100%;
    text-align: center;
}

#carousel-item-main a.dropdown-item {
    margin: 6px;
    width: calc(100% - 7%);
}

#carousel-item-main a.dropdown-item:hover {
    background-color: #1479FF;
    border: 0;
    color: #fff!important;
    border-radius: 6px;
    box-shadow: 0 3px 3px 1px rgba(20, 121, 255, 0.1);
}

.carousel-item.submenu.active .d-flex.flex-column.h-100 .header {
    font-weight: bold;
}

.popover-region-container {
    color: #193B68;
    border: 2px solid #193B68;
    box-shadow: 0 3px 3px 1px rgba(20, 121, 255, 0.1);
    font-weight: bold;
    padding: 12px;
    border-radius: 0.5rem;
}

.popover-region-content-container {
    -ms-overflow-style: none;
    scrollbar-width: none;
    overflow-y: scroll;
}

.popover-region-content-container::-webkit-scrollbar {
    display: none;
}

.popover-region-content .all-notifications .content-item-container.notification {
    padding: 4px;
    margin: 7px 0;
    border-radius: 0;
    border: 0;
}

.popover-region-content .all-notifications .content-item-container.notification:hover {
    border: 0;
    border-radius: 0.5rem;
    color: #fff!important;
    background-color: #1479FF;
    box-shadow: 0 3px 3px 1px rgba(20, 121, 255, 0.1);
}

.popover-region-content .all-notifications .content-item-container.notification a:hover {
    color: #fff!important;
}

#page-wrapper .drawer.bg-white {
    top: 90px;
    right: 14px;
    border-radius: 0.5rem;
    background-color: #fff!important;
/* 	filter: blur(2px); */
    backdrop-filter: blur(2px);
    background-color: rgba(255, 255, 255, 0.66)!important;
}

.message-app {
    background-color: transparent!important;
}

[data-region=right-hand-drawer].drawer {
    height: calc(100% - 107px);
}

.closewidget.text-right.pr-2 .text-dark.btn-link {
    color: #193B68!important;
}

.closewidget.text-right.pr-2 .text-dark.btn-link:hover {
    color: #1479FF!important;
}

.drawer-toggles .drawer-toggler {
    top: calc(60px + 2rem);
}

.drawer-toggles .drawer-left-toggle .btn, .drawer-toggles .drawer-right-toggle .btn {
    background-color: #fff;
    color: #193B68;
}

.drawer-toggles .drawer-left-toggle .btn:hover {
    color: #1479FF;
    box-shadow: 3px 0 3px 3px rgba(20, 121, 255, 0.1);
}

.drawer-toggles .drawer-right-toggle .btn:hover {
    color: #1479FF;
    box-shadow: -3px 0px 3px 3px rgba(20, 121, 255, 0.1);
}

.drawer-left, .drawer-right {
    top: calc(60px + 2rem);
    height: calc(100% - 107px);
    border-radius: 0.5rem;
    box-shadow: 3px 3px 3px 1px rgb(20 121 255 / 0.1);
    padding: 0 12px 12px 12px;
/* 	backdrop-filter: blur(2px);
    background-color: rgba(255, 255, 255, 0.66)!important; */;
}

.drawer.drawer-right {
    width: 320px;
    max-width: 320px;
}

.drawer.drawer-left {
    width: 280px;
    max-width: 280px;
}

.drawer.drawer-left.show {
    left: 14px;
}

.drawer.drawer-right.show {
    right: 14px;
    color: #193B68;
}

#page.drawers {
	margin-top: 0;
	scrollbar-width: none;
	scrollbar-color: #6a737b #f8f9fa;
	height: calc(100vh - 0px);
}

#page.drawers .main-inner {
	margin-top: 80px;
}

.navbar-toggler.aabtn.d-block.d-md-none.px-1.my-1.border-0:hover {
	color: #1479FF;
}

.drawer.drawer-left.drawer-primary.d-print-none.show .drawercontent.drag-container .list-group .list-group-item.list-group-item-action   {
	background-color: transparent;
	color: #193B68;
	border: 0;
	font-weight: bold;
}

.drawer.drawer-left.drawer-primary.d-print-none.show .drawercontent.drag-container .list-group .list-group-item.list-group-item-action.active {
	background-color: #1479FF;
	color: #fff;
	border-radius: 0.5rem;
	box-shadow: 3px 3px 3px 1px rgb(20 121 255 / 0.1);
}

.drawer.drawer-left.drawer-primary.d-print-none.show .drawercontent.drag-container .list-group .list-group-item.list-group-item-action:hover,  .drawer.drawer-left.drawer-primary.d-print-none.show .drawercontent.drag-container .list-group .list-group-item.list-group-item-action.active:hover {
	background-color: #fff;
	color: #1479FF;
	border: 2px solid #1479FF;
}

.drawercontent {
    -ms-overflow-style: none;
    scrollbar-width: none;
    overflow-y: scroll;
}

.drawercontent::-webkit-scrollbar {
    display: none;
}

.drawerheader {
    display: flex;
    justify-content: center;
}

.drawerheader .btn.drawertoggle.icon-no-margin {
    margin: 0.4rem;
    margin-top: 1rem;
    background-color: rgba(25, 59, 104, 0.1);
    color: #193B68;
    width: 100%;
}

.drawerheader .btn.drawertoggle.icon-no-margin:hover {
    background-color: #fff;
    border: 2px solid #193B68;
}

.courseindex .courseindex-item.pageitem {
    background-color: #1479FF;
    transition: 0.2s ease;
}

.courseindex .courseindex-item.pageitem a {
    color: #fff;
    transition: 0.2s ease;
}

.courseindex .courseindex-item.pageitem:hover {
    background-color: #193B68;
}

.courseindex .courseindex-item.pageitem a:hover {
    color: #fff;
}

li.nav-item.dropdown.dropdownmoremenu.show ul.dropdown-menu.dropdown-menu-left.show {
    left: -50px!important;
/* 	right: 1%; */
    border-radius: 0.5rem;
    border: 2px solid #193B68;
    font-weight: bold;
    padding: 6px;
}

ul.dropdown-menu.dropdown-menu-left.show li.nav-item a.dropdown-item:hover {
    background-color: #1479FF;
    border-radius: 0.5rem;
}

ul.dropdown-menu.dropdown-menu-left.show li.nav-item {
    margin: 0!important;
}

.secondary-navigation {
    max-width: 100%!important;
}

#page {
    -ms-overflow-style: none;
    scrollbar-width: none;
    overflow-y: scroll;
}

#page::-webkit-scrollbar {
	display: none;
}

}
