css:
body {letter-spacing:0.1px}
a { outline:0 !important; }
#logo a { 
   background:url(data:image/png;base64,%3D%3D) no-repeat 0 0;
   text-indent:-9999px;
   height:26px;
   margin:20px 0 0 20px;
   width:154px;
   display:block;
   outline:0;
}
#logo a img { display:none; }

.actions { font-size:12px !important }

#top_right a, #bottom, #bottom a { color:#b5b5b5 !important; font-size:11px !important }

.items .current,
.AmiMenu .on {
  background-color:#fff9ba !important;
  color:#202020 !important;
}

#agenda_box .search_img,
.print_icon,
#agenda_box .more img,
#bottom,
.history, .his_ctrl,
.GB_Window .close span,
li.no_appoinments
 { display:none !important; }

#agenda_box { margin:0 !important; }
#agenda_box .input_q { width:195px !important; }

#content {position:relative;}
#editor .controller, #project_man { margin:15px 0 0 5px !important; }
#editor .controller { margin-left:9px !important; }
#editor .controller .more {position:absolute; top:-12px; right:30px; color:#b5b5b5 !important; font-size:11px; }
#editor .controller .more img { display:none;}

h2 {
  font-size:17px !important;
  font-weight:normal;
  margin:0 0 5px !important;
  padding:3px 7px !important;
  display:inline-block;
  border-bottom:0px solid #fff9ba;
}

.h2_date {
   color:#b5b5b5 !important;
   font-size:11px;
   margin-left:10px !important;
}

.priority_2 {border-left: 3px solid #FFF000 !important;}
.priority_3 {border-left: 3px solid #FFC000 !important;}
.priority_4 {border-left: 3px solid #FF2900 !important;}

.list_editor .items li.priority_4, 
.list_editor .items li.priority_3, 
.list_editor .items li.priority_2,
.items li.priority_4, 
.items li.priority_3, 
.items li.priority_2 {
   color: black !important;
   padding-left: 2px !important;
}

#agenda_view .separator { margin:30px 0 0 !important; }
#agenda {margin-bottom:20px !important;}
.items li { border-bottom-width:0 !important; /* padding-top:7px !important; padding-bottom:7px !important; */ }
#project_list li { 
   border-bottom-width:0px !important;
   padding-top:5px !important;
   padding-bottom:5px !important;
   color:#7a7a7a !important;
}
#project_list li.current { color:#000 !important; background:transparent !important;  }
#project_list li.current .name { font-weight:bold; font-size:12.5px !important; }

.items li.manager { padding-left:24px !important; padding-top:2px !important; padding-bottom:0 !important; }
.day_list li.manager { padding-left:28px !important; padding-top:3px !important; padding-bottom:0 !important; }
#project_list li.manager { padding-left:5px !important;}
.checker { display:block; width:20px !important; }
.checker input { margin-top:0px !important; }
.agenda_checker input { margin-top:1px !important; }

.auto_exapnd_ta, .auto_exapnd_clone {
font-family: Arial,Helvatica,sans-serif !important;
font-size:13px !important;
letter-spacing:0.1px !important;
}

.arrow { margin-left:-19px !important }
.AmiMenu { background-color:#FFFFFF !important; }
#top #top_right { display:none!important; }
#top:hover #top_right { display:block !important; }
.GB_Window .header { background:#fff9ba !important; }
.GB_Window .caption { padding-left:7px }
