css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("good-tutorials.com") {

* {
font-family: arial !important;
}

body {
width: 860px !important;
margin: auto !important;
background: #CCEAFD !important;
}

#top {
width: auto !important;
margin: 0 15px 10px !important;
}

#header {
padding-bottom: 4px !important;
-moz-border-radius: 0 0 7px 7px !important;
border-radius: 0 0 7px 7px !important;
}

#logo {
padding: 0 !important;
}

#header ul {
padding: 7px 0 0 0 !important;
}
#header li {
font-size: 13px !important;
}

.search input {
margin-bottom: 0 !important;
}

#quicklogin {
top: 11px !important;
left: 370px !important;
width: auto !important;
}

.teaser {
font-size: 15px !important;
}
.teaser a:hover {
color: #222 !important;
}

#topics {
float: right !important;
padding: 0 !important;
margin: 10px 0 10px 10px !important;
background: #fff !important;
text-align: right !important;
text-align: left !important;
-moz-border-radius: 7px !important;
border-radius: 7px !important;
}
#topics ol {
float: left !important;
border: 0 !important;
}
#topics li {
float: none !important;
display: block !important;
font-weight: bold;
font-size: 1.2em !important;
padding: 0 !important;
}
#topics li a,
#topics li a:link {
border: 0 !important;
display: block !important;
padding: 5px 7px !important;
color: #777 !important;
}
#topics li:last-child a,
#topics li:last-child a:link {
-moz-border-radius: 0 0 5px 5px !important;
border-radius: 0 0 5px 5px !important;
}
#topics li a:hover {
color: #004577 !important;
background: #CCEAFD !important;
}
#upcoming {
float: none !important;
display: block !important;
font-size: 1.2em !important;
padding: 0 !important;
border: 0 !important;
color: #777 !important;
text-shadow: 0 0 0 transparent !important;
-moz-border-radius: 5px 5px 0 0 !important;
border-radius: 5px 5px 0 0 !important;
overflow: hidden !important;
}
#upcoming a:first-letter {
text-transform: uppercase !important;
}
#upcoming a {
display: block !important;
padding: 5px 7px !important;
text-shadow: 0 0 0 transparent !important;
-moz-border-radius: 5px 5px 0 0 !important;
border-radius: 5px 5px 0 0 !important;
}
#upcoming a:hover {
color: #004577 !important;
background: #CCEAFD !important;
}


#wrapper {
width: auto !important;
margin-left: 15px !important;
padding: 0 !important;
overflow: hidden !important;
}


#container {
float: left !important;
width: 100% !important;
margin: 0 !important;
font-size: 20px !important;
}
.content p {
font-size: 17px !important;
}
.cap {
padding-top: .3em !important;
}
.cap h1 {
font-size: 22px !important;
}

#sidebar {
width: 179px !important;
float: right !important;
margin: 0 -179px 0 0 !important;
}

.sidenote {
padding: 3px !important;
}
.sidenote:after {
clear: both !important;
display: block !important;
visibility: hidden !important;
content: "&nbsp" !important;;
height: 0 !important;
}

#skycolumn {
width: 100% !important;
}
#skycolumn ul {
display: none !important;
}
#skycolumn ul:first-child {
display: block !important;
margin: 0 !important;
}
#skycolumn li {
font-weight: bold;
font-size: 1em !important;
}
#skycolumn li a {
color: #777 !important;
padding: 5px 7px !important;
-moz-border-radius: 0 !important;
border-radius: 0 !important;
}
#skycolumn li a:hover {
color: #004577 !important;
background: #CCEAFD !important;
}
#skycolumn li:last-child a {
-moz-border-radius: 0 0 5px 5px !important;
border-radius: 0 0 5px 5px !important;
}
#skycolumn li.headline {
font-size: .9em !important;
padding: 5px 6px !important;
margin: 0 !important;
background: #fff !important;
}

.sidechunk:nth-of-type(3) {
margin: 0 !important;
}


#footer {
font-size: .95em !important;
padding: 10px 0 !important;
margin: 0 0 10px 0 !important;
border: 0 !important;
text-align: center !important;
background: transparent !important;
-moz-border-radius: 7px !important;
border-radius: 7px !important;
}


/*** mid columns styles ***/
.cap {
padding: 10px 15px !important;
border: 0 !important;
background: #fff !important;
}
.sort {
padding: 0 13px !important;
border: 0 !important;
border-left: 3px solid #fff !important;
border-right: 3px solid #fff !important;
background: #eee !important;
}
.sort a {
display: inline-block !important;
padding: 2px 4px 3px !important;
text-decoration: none !important;
}
.sort a:hover {
color: #333 !important;
background: #fff !important;
}
.sort a:first-child {
float: right !important;
padding: 0 !important;
margin: 4px 0 0 0 !important;
}


.featured {
margin: 10px -15px 0 !important;
}
.featured .tutorial {
padding: 10px 14px !important;
background: #CCEAFD !important;
}


.content {
padding: 0 14px !important;
border: 0 !important;
border-left: 3px solid #fff !important;
border-right: 3px solid #fff !important;
overflow: hidden !important;
}
.tutorial {
padding: 15px 0 20px !important;
overflow: hidden !important;
}
.block {
padding: 3px 0 7px !important;
}
.tutorial .thumbnail img {
margin: 5px 11px 0 0 !important;
}
.tutorial .author {
margin: 0 !important;
}
.description {
color: #222 !important;
margin: 0 0 0 60px !important;
font-size: 16px !important;
line-height: 23px !important;
}
.stats {
  display: none !important;
}
.tutorial .stats a:last-child {
margin: 0 !important;
}
.upcoming {
margin: 5px 0 15px !important;
}




.cube,
#adspan,
#skyscraper,
iframe,
.sidechunk:last-child,
.sidechunk:nth-of-type(2),
.sidechunk:nth-of-type(3),
#sidebar {
display: none !important;
}

}
