css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sgjpn.net") {
/*
SGJPN.net ANH Style ver1.01
Author:HIDEMI
URL:anh.seesaa.net
*/

body,td { margin:0 !important; padding:0 !important; font-family:Verdana, Helvetica, Sans-Serif !important; font-size:12px !important; color:#333 !important; }

a:link { background-color:inherit !important; color:#369 !important; text-decoration:none !important; }
a:active { background-color:transparent !important; color:#369 !important; text-decoration:none !important; }
a:visited { background-color:inherit !important; color:#369 !important; text-decoration:none !important; }
a:hover { background-color:transparent !important; color:#369 !important; text-decoration:underline !important; }

h1, h2, h3, h4, h6 { background-color:#ddd !important; color:#333 !important; }
h5 { padding-bottom:2px !important; background-color:transparent !important; border-bottom:2px solid #999 !important; font-size:12px !important; }

.style_table { margin:1px !important; padding:0 !important; background-color:#ccc !important; border:0 !important; text-align:left !important; color:inherit !important; }
.style_td { margin:1px !important; padding:5px !important; background-color:#eef5ff !important; color:inherit !important; }

hr.note_hr { width:100% !important; margin:10px 0 5px 0 !important; border-width:1px 0px !important; border-style:ridge; border-color:#333 !important; text-align:center !important; }
hr.full_hr { margin:10px 0 5px 0 !important; border-width:1px 0px !important; border-style:ridge !important; border-color:#333 !important; }


/* pukiwiki.skin.*.php */
td#header1 { background-color:#333 !important; text-align:left !important; vertical-align:middle !important; border-right:0px none #333 !important; }
td#header2 { background-color:#333 !important; text-align:center !important; vertical-align:middle !important; }
td#header2 embed { display:none !important; }
td#headerbar { height:26px; background-color:#ddd !important; vertical-align:middle !important; border-top:1px solid #000 !important; border-right:0px none #333 !important; border-bottom:1px solid #999 !important; font-size:11px !important; }
td#headerbar font { color:#666 !important; }
td.menubar { width:170px; background-color:#f4f4f4 !important; border-right:0px none #333 !important; border-left:0px none #333 !important; }
td.menubar div { padding-bottom:10px !important; }
div#menubar { width:160px !important; margin-top:10px !important; font-size:12px !important; overflow:hidden !important; }
div#menubar ul { margin:0 0 0 12px !important; padding:0 0 0 5px !important;}
div#menubar ul li {line-height:110% !important;}
div#menubar h4 {font-size:110% !important;}
div#body { width:700px !important; margin-top:10px !important; padding-left:10px !important; }
div#body input { font-size:12px !important; }
div#note { padding:0 10px 10px 10px !important; }
div#attach { padding:0 10px 10px 10px !important; }
td#footer { padding:0 !important; background-color:#ddd !important; border-top:1px solid #999 !important; border-bottom:1px solid #999 !important; }
div#toolbar { height:26px !important; padding:0 10px 5px 10px !important; text-align:left !important; }
div#lastmodified { padding:0 10px 5px 10px !important; font-size:11px !important; }
div#related { padding:0 10px 5px 10px !important; font-size:11px !important; }
div#footer { padding:0 10px 5px 10px !important; font-size:11px !important; }
div#preview { background-color:#f5f8ff !important; color:inherit !important; }

}
