css:
@-moz-document domain("thehindu.com")
 {
@media only screen and (min-width: 767px) {
.navbar, .socialize, .media-body, .aside, .also-view-container, .ad-ft, .outbrain-main-cont, .stickyfooterarticlealone, .editvalue, .trc_related_container, .tp-container-inner, .container-sticky #artmeterwrap {
        display: none;
}

.article-header-cont, .home {
        display: contents;
    }
    .author-container, .article-block-item {
        box-sizing: content-box;
        padding: 1% 10%;
        text-align: center;
        width: 80%;
        background-color: #FFF0;
    	position: relative;
    }
    .lead-img-cont,.lead-img-cont img, .img-full-width, .lead-img-caption > p {
        margin-left: auto;
        margin-right: auto;
    }
    .article-block, .ipso-facto, .article::after, .artcl-social-media, .author-bottom, .morein-tag-cont, .outbrain-main-cont, .article h1, .article, .article-cont p, .article h2.intro, .article h2, .navbar {
		margin-left: 5%!important;
        margin-right: 5%!important;
        width: auto;
        background: #ffffff00;
        float: none;
    }
    .exit-point-container, .exit-point-container .e-p-slide-text::after, .exit-point-container:after, .related-artcl-cont:after, .morein-tag-cont .e-p-slide-text::after, .related-artcl-cont {
        background: none;
    }
}
@media only screen and (min-width: 1px) {
    .just-in-counter, .footerbg, p.editvalue, .outbrain-main-cont, .ad-ft, .nxt-prv-2-story-cont, .arutCommRight, .trc_related_container, .tp-container-inner, .mod-top-add {
        display: none;
    }
    .article h2, .article-cont p, .article p {
        padding: 1% 8%;
    }
 }
}
