css:
@-moz-document domain('www.minecartrapidtransit.net') {

/*@media(prefers-color-scheme: light) {*/
 body {
    color: #eee;
    background-color: #222;
}
.tribe-common * {
    color: #eee !important;
}
.tc-header {
    background: #222 !important;
    border-bottom-color: #555 !important;
}
h1, h2, h3, h4, h5, h6, .tc-sidebar .widget li a {
    text-shadow: 0 1px 2px #555;
}
.navbar .nav>li>a, .social-block a {
    text-shadow: 0 1px 2px #555;
    color: #eee
}
.tc-sidebar .widget li:hover, .widget li:focus {
    text-decoration: none;
    background-color: #666;
}
a {
    color: #3bf !important;
}
a:focus, a:hover {
    color: #0af !important;
}
tr:nth-child(odd) td, tr:nth-child(odd) th {
    background-color: #555 !important;
    color: #fff
}
tr:nth-child(even) td, tr:nth-child(even) th {
    background-color: #444 !important;
    color: #fff
}
blockquote {
    background-color: #555;
    color: #fff
}
footer#footer {
    color: #eee!important;
    border-top: 10px solid #555;
    background: black;
}
.colophon {
    background-color: #222 !important;
}
.span6.credits p {
    color: #eee !important;
}
a.site-logo {
    position: relative;
    width: 200px;
    height: 200px;
    margin-top: 20px !important;
    margin-bottom: -200px !important;
    margin-left: 20px !important;
    overflow: hidden;
    transition: transform 0.2s !important;
    will-change: transform;
}
.attachment-1227 {
    min-height: 100%;
    min-width: 300%;
    position: absolute;
    left: 0;
    bottom: 0;
}
.attachment-1228 {
    filter: opacity(0);
}
.sticky-enabled .tc-sticky-logo-on .site-logo img {
    display: block;
}
.sticky-enabled .tc-sticky-logo-on .site-logo {
    transform: scale(0.5) translate(-80px, -100px);
}
.tc-sidebar {
    margin-top: 100px;
}
div#ez-toc-container {
    background-color: #111;
    border: 1px solid #666;
}
.nav-collapse.tc-hover-menu-wrapper.in.collapse {
    background: #333c;
    border: 1px solid gray;
}
div#tribe-events-content * {
    color: white;
}
div#tribe-events-content {
    margin-top: 40px;
}
@media(max-width: 979px) {
    .site-logo {
        width: 100px !important;
        height: 100px !important;
        top: 12px;
    }
    .social-links {
        position: relative;
        left: 125px;
    }
    .tc-header .container {
        margin-left: 140px;
    }
    h2.site-description {
        text-align: left;
    }
}
}
