css:
@-moz-document url("https://youandme.chat/") {
div[id^="bz1"], div[id^="vk_like"], div[class^="fb-like"], div[class^="vk_subscribe_wrap"], div[id^="vk_like_"], div[id^="vk_subscribe_group_"] {display: none !important}

div[id^="bz1"], div[id^="vk_like"], div[class^="fb-like"], div[class^="vk_subscribe_wrap"], div[id^="vk_like_"], div[id^="vk_subscribe_group_"] {display: none !important}

.hasAsideChatMedia .logbox {right:0px}

body {background:#282B2F repeat 50% 50%;;background-position:0px 0px;background-attachment:fixed}
.hasBgMedia #chatPage {background:none;}

#page {
	padding: 0;
	width: 836px;
	margin: 0px auto;
	background: #282B2F; /*#282B2F*/
	position: relative;
	z-index: 2;
}


#main {
	background: #282B2F;
	margin: 0 20px;
    border-radius: 15px;
}

.section {
	width: 640px;
    margin: 0 auto 26px;
    background: #303338;
    padding: 30px 48px;
    border-radius: 15px;
}

#room-options {
	float: right;
    border: 1px solid #282B2F;
    border-radius: 12px;
}

.room-option {
    position: relative;
    display: inline-block;
    vertical-align: top;
    min-width: 156px;
    box-sizing: border-box;
    text-align: center;
    padding: 0 22px;
    line-height: 42px;
    font-size: 14px;
    color: #ffffff;
    cursor: pointer;
    transition: background 0.09s, color 0.09s;
    -webkit-transition: background 0.09s, color 0.09s;
}


.room-option:not(:first-child) {
    border-left: 1px solid #303338;
}


.room-option.selected {
    z-index: 1;
    background: #282B2F;
    line-height: 44px;
    padding: 0 24px;
    margin: -1px;
    color: #FFF;
}

#header {
	font-size: 1.2em;
	color: #fff;
}

#header p strong {
	margin-left: 5px;
	color: #fff;
	background: #282B2F;
	padding: 0 5px;
	font-size: 20px;
	vertical-align: -2px;
    border-radius: 3px;
}

#header h2 {
	float: left;
	color: #fff;
	font-size: 16px;
	font-weight: normal;
	height: 70px;
	margin: 0 0 0 10px;
	padding: 0;
	line-height: 76px;
}

.startChat a {
	display: inline-block;
	padding: 10px 30px;
	font-size: 26px;
	color: #FFF;
	font-weight: bold;
    border-radius: 15px;
	background: #282B2F;
	text-decoration: none;
}

.ended-but, .light-button {
    display: inline-block;
    height: 28px;
    line-height: 28px;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    margin: 5px 5px 0 0;
    padding: 0 10px;
    border: 1px solid #303338;
    border-radius: 8px;
    font-size: 12px;
    color: #fff;
    text-decoration: none;
    background: #282b2f;
    cursor: pointer;
    -webkit-transition: background 0.135s;
    -o-transition: background 0.135s;
    -moz-transition: background 0.135s;
    transition: background 0.135s;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}

#chat-settings-content {
    display: none;
    opacity: 0;
    position: absolute;
    z-index: 3;
    top: 0;
    left: 0;
    margin-top: 36px;
    background: #303338;
    border-radius: 8px;
    width: 330px;
    border: 1px solid #303338;
    text-align: left;
}

.settings-row.border {
    border-bottom: 1px solid #303338;
}

.topic-options-button:hover, .topic-options-button:active {
	background: #303338;
}

#topic-to-top > .switch:not(.on) i {
    background: #303338;
}

#topic-to-top > .switch:not(.on) i::after {
    border-color: #303338;
}

.vip-tagline {
    position: relative;
    margin: 2px 0;
    min-height: 15px;
    max-height: 30px;
    overflow: hidden;
    font-size: 12px;
    line-height: 15px;
    font-weight: 400;
    color: #ffffff;
    overflow: hidden;
    transition: opacity 0.135s;
    display: -webkit-box;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: 2;
}

.vip-name {
    margin: 1px 0;
    font-size: 13px;
    font-weight: 600;
    color: #ffffff;
    white-space: nowrap;
    overflow: hidden;
}

.signin-content {
    width: 100%;
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    width: 100%;
    overflow-y: auto;
    -webkit-overflow-scrolling: touch;
    background: #282b2f;
    border-radius: 10px;
    text-align: center;
}

#current-user {
    position: absolute;
    top: 0;
    right: 0;
    left: 0;
    height: 86px;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: #282b2f;
    z-index: 2;
}

#side {
    position: absolute;
    top: 0;
    bottom: 0;
    z-index: 12;
    width: 300px;
    background: #282b2f;
}

body.start-page #side-toggle.side-shown::after {
    content: "";
    position: absolute;
    top: -8px;
    right: -8px;
    bottom: -8px;
    width: 8px;
    background: #282b2f;
}



#side-toggle {
    display: block;
    position: absolute;
    top: 30px;
    right: 0;
    height: 46px;
    width: 46px;
    background: #303338;
    border-radius: 9px 0 0 9px;
    line-height: 46px;
    text-align: center;
    font-size: 26px;
    color: #FFF;
    color: rgba(255, 255, 255, 0.9);
    cursor: pointer;
    z-index: 13;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    -webkit-transition: top 0.18s, right 0.18s, -webkit-box-shadow 0.18s;
    transition: top 0.18s, right 0.18s, -webkit-box-shadow 0.18s;
    -o-transition: top 0.18s, right 0.18s, box-shadow 0.18s;
    -moz-transition: top 0.18s, right 0.18s, box-shadow 0.18s;
    transition: top 0.18s, right 0.18s, box-shadow 0.18s;
    transition: top 0.18s, right 0.18s, box-shadow 0.18s, -webkit-box-shadow 0.18s;
}
.phone-wrap .country-wrap select {
    -webkit-appearance: none;
    position: relative;
    z-index: 1;
    width: 100%;
    height: inherit;
    line-height: 36px;
    padding: 0 2px;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    border: 1px solid;
    border-color: #282b2f #282b2f #282b2f #282b2f;
    background: none;
    border-radius: 0;
    font-family: inherit;
    font-size: 13px;
    color: rgba(0, 0, 0, 0);
    cursor: pointer;
}

#nickname::after {
    content: "";
    position: absolute;
    top: 0;
    right: -1px;
    bottom: 0;
    width: 25px;
    background: -webkit-linear-gradient(left, rgba(212, 73, 62, 0) 0%, rgb(40 43 47) 100%);
    background: -webkit-gradient(linear, left top, right top, from(rgba(212, 73, 62, 0)), to(rgb(40 43 47)));
    background: -moz-linear-gradient(left, rgba(212, 73, 62, 0) 0%, rgba(212, 73, 62, 1) 100%);
    background: linear-gradient(to right, rgba(212, 73, 62, 0) 0%, rgb(40 43 47) 100%);
}

.menu-header {
    width: 100%;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    margin: 15px 0;
    padding: 0 15px;
    font-weight: 400;
    font-size: 14px;
    text-transform: uppercase;
    color: #fff;
    text-align: left;
}

.signin-button {
    display: inline-block;
    min-width: 90px;
    height: 36px;
    line-height: 36px;
    margin: 24px 0 15px 0;
    padding: 0 15px;
    border-radius: 9px;
    font-size: 13px;
    color: #fff;
    text-decoration: none;
    text-transform: uppercase;
    background: rgb(48 51 56);
    cursor: pointer;
    -webkit-transition: background 0.135s;
    -o-transition: background 0.135s;
    -moz-transition: background 0.135s;
    transition: background 0.135s;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}

.field-wrap input {
    display: block;
    position: absolute;
    top: 24px;
    left: 0;
    z-index: 1;
    width: 100%;
    height: 36px;
    line-height: 36px;
    padding: 0 2px;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    border-bottom: 1px solid #282b2f;
    background: none;
    border-radius: 0;
    font-family: inherit;
    font-size: 13px;
    color: #fff;
    -webkit-transition: border-bottom-color 0.135s, background 0.135s;
    -o-transition: border-bottom-color 0.135s, background 0.135s;
    -moz-transition: border-bottom-color 0.135s, background 0.135s;
    transition: border-bottom-color 0.135s, background 0.135s;
}

#log {
	position: absolute;
	left: 0; right: 0; top: 0; bottom: 0;
	padding: 15px 17px 0 17px;
	background: #282B2F;
	word-wrap: break-word;
	z-index: 0;
	overflow-y: auto;
	-webkit-overflow-scrolling: touch;
}

#actions {
    position: absolute;
    top: 85px;
    right: 0;
    left: 0;
    height: 50px;
    background: #282b2f;
    border-radius: 12px 12px 0 0;
    text-align: right;
    white-space: nowrap;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}

#sections-chat-container {
    position: relative;
    min-width: -webkit-min-content;
    min-width: -moz-min-content;
    min-width: min-content;
    background: #282b2f;
    padding: 23px 0;
    margin: 0 6px;
    border-radius: 8px;
}

#controls {
    position: absolute;
    right: 0;
    left: 0;
    bottom: 0;
    height: 64px;
    background: #282b2f;
    border-radius: 0 0 12px 12px;
    z-index: 2;
}

#log ol > li {
    position: relative;
    list-style-type: none;
    margin: 0;
    white-space: nowrap;
    min-height: 26px;
    padding: 0 50px 0 83px;
    border-bottom: 1px dotted #282b2f;
    font-size: 13px;
    color: #141414;
}


#log ol > li {
    position: relative;
    list-style-type: none;
    margin: 0;
    white-space: nowrap;
    min-height: 26px;
    padding: 0 50px 0 83px;
    border-bottom: 1px dotted #282b2f;
    font-size: 13px;
    color: #ffffff;

}
#log ol > li.from {
	background: #840b0b;
}
#log ol > li.to {
	background: #282B2F;
}

#log ol > li.from {
    background: #282b2f;
}

#typing, #status-chat-started, #opponent-label > span, #opponent-panel-chat-ended {
    line-height: 50px;
    color: #fffefe;
    font-size: 13.5px;
    font-weight: 400;
    white-space: nowrap;
}

#online-count {
    display: inline;
    margin-left: 10px;
    background: #282b2f;
    padding: 1px 6px;
    font-size: 20px;
    color: #ffffff;
    font-weight: 700;
    vertical-align: baseline;
    border-radius: 4px;
}

#online {
    float: right;
    height: 70px;
    line-height: 63px;
    margin: 7px 0 0 15px;
    font-weight: 400;
    font-size: 16px;
    color: #282b2f;
    color: rgb(255 255 255);
}

#desc {
    float: left;
    height: 70px;
    line-height: 63px;
    margin: 7px 0 0 15px;
    font-weight: 400;
    font-size: 16px;
    color: #282b2f;
    color: rgb(255 255 255);
}

#add-to-contacts, #settings, #friend-actions {
    border-radius: 6px;
    padding: 5px 8px;
    font-size: 13px;
    font-weight: 400;
    text-transform: uppercase;
    color: #fff;
    cursor: pointer;
    -webkit-transition: background 0.135s;
    -o-transition: background 0.135s;
    -moz-transition: background 0.135s;
    transition: background 0.135s;
}

#topic-current-chat > span {
    display: inline-block;
    height: 28px;
    line-height: 28px;
    margin-left: 6px;
    padding: 0 10px;
    background: #303338;
    border-radius: 8px;
    text-transform: none;
    font-size: 14px;
    color: #ffffff;
}

#log-wrap::before {
    content: "";
    position: absolute;
    top: 0;
    right: 17px;
    left: 17px;
    height: 10px;
    background: none;
    background: -webkit-gradient(linear, left top, left bottom, from(#FFF), to(rgb(40 43 47)));
    background: -webkit-linear-gradient(top, #282b2f 0%, rgb(40 43 47) 100%);
    background: -moz-linear-gradient(top, #FFF 0%, rgba(255, 255, 255, 0) 100%);
    background: linear-gradient(to bottom, #282b2f 0%, rgba(255, 255, 255, 0) 100%);
    z-index: 2;
    pointer-events: none;
}

#topic-current {
    display: inline-block;
    height: 28px;
    line-height: 28px;
    margin-left: 18px;
    padding-left: 10px;
    background: #303338;
    border-radius: 8px;
    font-size: 14px;
    color: #f2f3f3;
}

.but-chat-start, .dark-button {
    display: inline-block;
    height: 32px;
    line-height: 32px;
    margin-bottom: 5px;
    margin-right: 10px;
    vertical-align: middle;
    padding: 0 15px;
    border-radius: 9px;
    font-size: 13px;
    color: #FFF;
    text-decoration: none;
    background: rgb(40 43 47);
    cursor: pointer;
    -webkit-transition: background 0.135s;
    -o-transition: background 0.135s;
    -moz-transition: background 0.135s;
    transition: background 0.135s;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}

::-webkit-scrollbar {
  width: 0;
}

#title {
    height: 70px;
    float: left;
    font-size: 45px;
    line-height: 70px;
    font-weight: 700;
    color: #ffffff;
}

#room-current-chat > span {
    text-transform: none;
    margin-left: 7px;
    color: #ffffff;
}

.section-header {
    margin: 0 0 20px;
    font-size: 15px;
    line-height: 18px;
    color: #fff;
    text-transform: uppercase;
}

#footer {
    font-size: 14px;
    color: #303338;
}

#footer a {
    color: #303338;
}

#text-wrap {
	position: absolute;
	top: 0; right: 128px; bottom: 0; left: 220px;
	height: 60px;
	margin: auto 0;
	padding: 4px;
	-webkit-box-sizing: border-box;
	   -moz-box-sizing: border-box;
	        box-sizing: border-box;
	background: #303338;
	overflow: hidden;
    border-radius: 6px;
}


#text-wrap.disabled {
	background: #FFFFCC;
	background: rgb(48 51 56);
}

#text {
    position: absolute;
    top: 0;
    right: 124px;
    bottom: 0;
    left: 0;
    margin: 3px 5px;
    font-size: 15px;
    color: #ffffff;
    white-space: pre-wrap;
    word-wrap: break-word;
    overflow-x: hidden;
    overflow-y: auto;
    cursor: text;
}
}
