http://trendy.nikkeibp.co.jp/
*/

@-moz-document domain("trendy.nikkeibp.co.jp") {
/* template v4 2008-05-08 */
* {
background-image : none !important;
float : none !important;
text-align : left !important;
letter-spacing : 1px !important;
line-height : 1.5 !important;
min-width : 0 !important;
text-indent : 0 !important;
white-space : normal !important;
list-style-image : none !important;
}
pre {
white-space : pre !important;
overflow : auto;
}
html {
font-size : 100% !important;
background-color : #e4e2d4 !important;
}
body {
font-size : 112.5% !important;
margin : 0 auto !important;
padding : 0 !important;
background-color : #e4e2d4 !important;
color : #000 !important;
width : 40em !important;
max-width : 100% !important;
border : 0 !important;
}
table[width]
, tbody[width]
, thead[width]
, tr[width]
, td[width]
{ width : auto !important; }
noscript
, iframe
, hr
, br[clear="all"]
, #footer
, #banner
, .footer
, .banner
, img[width="1"]
, img[width="0"]
, img[src^="http://ad"]
, *[id^="ad"]
, *[class^="ad"]
, *[id*="calendar"]
, *[class*="calendar"]
, *[id*="track"]
, *[class*="track"]
{ display : none !important; }
u { text-decoration : none !important; }
a { text-decoration : underline !important; }
font[size] { font-size : 100% !important; }
table[width] { max-width : 40em !important; }
/* /template */

/* destroy div layout */
#content
, #content div {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
border : 0 !important;
margin : 0 !important;
padding : 0 !important;
}
#content * {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
color : #000 !important;
}
#content a:link { color : #0000ee !important; }
#content a:visited { color : #551a8b !important; }
#content a:hover { color : #ee0000 !important; }
#content a:hover { background-color : transparent !important; }
/* /destroy div layout */

body > div[style="width: 1000px;"]
, .lnavi01
, ul#digital
, .common_content
, .leaf_header
, .keyword
, #rightarea
, .top_books
, .list_navi
, .top_anchor
, .labtv
, #leaftype
, #bpstore
, .article_recent_thumbs a img
, .article_recent_thumbs .genre
, #header a img
, #h_logo > p
{ display : none !important; }

#header
{ margin : 0 !important; }

#header
, .article_recent_thumbs p.foot_more
{ padding : 0 !important; }

#header
, #h_logo
{ width : auto !important; }

#header
, #h_logo
{ min-height : 0 !important; }

.article_recent_thumbs p.foot_more
{ border : 0 !important; }

#content .article_recent
, #content .article_recent_thumbs
{ margin : 1em 0 !important; }

#header a:before
{ content : "æ—¥çµŒãƒˆãƒ¬ãƒ³ãƒ‡ã‚£ãƒãƒƒãƒˆ"; }

}
