css:
@-moz-document domain("javlibrary.com") {
    /* Insert code here... */
    :root {
        --main-bg: #131313;
        --main-fg: #fefefe;
    }

    #toplogo img {
        display: none;
    }

    #toplogo .sitelogo a:before {
        content: "JAVLibrary";
        font-weight: bold;
        font-size: 22px;
    }
    

    /* Main dark background */
    #toplogo,
    #content,
    .videothumblist .videos .video:hover,
    div.textbox,
    div.titlebox,
    .searchbox,
    div.starbox,
    .searchitem,
    #fm_pm {
        background: var(--main-bg) !important;
        color: var(--main-fg) !important;
    }

    /* White FG */
    #video_title h3.post-title a {
        color: var(--main-fg) !important;
    }

    /* Dark border color */
    #rightcolumn,
    div.textbox,
    #video_title,
    #video_info table,
    .videothumblist .videos .video,
    .searchitem {
        border-color: #444;
    }

    hr.grey {
        background-color: #252525;
    }
    
    .searchitem:hover {
        background: #272727;
    }
    
    .pubgroup .dimrow {
        background: #161616;
    }
    .pubgroup a,
    .txt_formcap {
        color: #808080;
    }

    .genre,
    .cast,
    .smallbutton,
    .largebutton,
    #idsearchbutton,
    #video_reviews_alllang,
    #video_comments_alllang,
    .genreitem,
    #video_comments_all,
    .page_selector .page {
        background: #252525;
        border: none;
        box-shadow: none;
        cursor: pointer;
    }
    .smallbutton:hover,
    .largebutton:hover,
    .genreitem:hover,
    #idsearchbutton:hover,
    #video_comments_all:hover,
    .page_selector .page:hover {
        background: #272727;
        color: var(--main-fg);
        border: none;
    }

    #video_comments table.comment {
        border-color: #444;
    }
    #video_comments td.info {
        border-right: 1px dotted #444;
    }
    #video_comments td.date {
        border: none;
    }

    a {
        color: #ccc;
    }

    a:hover {
        color: var(--main-fg);
    }
    
    .video a:visited {
        color: #656e77;
    }

    #idsearchbox,
    .searchbox input[type="text"] {
        background: none;
        border: 1px solid #666;
        color: var(--main-fg);
        padding: 0 4px;
    }

    #idsearchbox:focus {
        background: none;
    }

    #leftmenu .menul1 {
        background-color: #222;
        color: #999
    }
    #leftmenu li {
        border: none;
    }
    #leftmenu li a {
        background: none;
        border: none;
        margin-left: 20px;
    }
    #leftmenu li a:hover {
        background: #454545;
        border: none;
    }
    .quotetitle {
        background: black !important;
    }

    table.about td.desc {
        background-color: #443844;
    }
    table.about td.desc1 {
        background: #252525;
    }
    
    .videotextlist tr.dimrow {
        background-color: #1c1c1c;
    }
}
