css:
@-moz-document domain("roblox.com")
{
  .ad,.ad *,.img_ad,.ad-annotations,#google_image_div,#aw0,#AdvertisingLeaderboard
  {
    display: none !important;
  }

  .ad,.ad *,.img_ad,.ad-annotations,#google_image_div,#aw0,#AdvertisingLeaderboard
  {
    display: none !important;
  }
 
  #header
  {
    background-color: #000000;
  }

  .navigation .notification-icon,
.rbx-highlight
  {
    background: #000000;
  }

  #chat-header
  {
    background: #000000;
    ;
  }

  .slide-item-container-left,
.profile-avatar-right
  {
    background: #000000;
  }

  .rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading,
.rbx-tabs-horizontal .rbx-tab-heading:hover,
.rbx-tabs-horizontal .rbx-tab-heading:focus,
.rbx-tabs-horizontal .rbx-tab-heading:active
  {
    box-shadow: 0 -4px 0 .6 #000000 inset;
    ;
  }

  .forum-table-header,
body .forum-table-header th,
body .forum-table-footer,
#ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th,
#ctl00_cphRoblox_CenterColumn > table > tbody > tr:nth-child(1) > th
  {
    background: #000000 !important;
    ;
  }

 
 
  /*navigation bar border*/
  #navbar-search-input,
#navbar-search-btn
  {
    border: 1px solid #000000;
    ;
  }

/*make background transparent*/
  body
  {
    background: transparent !important;
                       
  }

/*main content block*/
  .content
  {
    background: transparent !important;
  }

/*background image*/
  #fb-root
  {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url("https://purewows3.imgix.net/images/articles/2021_09/squid_game_review_400.jpg?auto=format,compress&cs=strip") !important;
    background-size: cover !important;
    background-attachment: fixed !important;
  }

/*message box*/
.light-theme .message-detail {
    background-color: black;
}

/*tool bar above message box*/
.light-theme .rbx-tabs-horizontal .rbx-tab .rbx-tab-heading {
    background-color: black;
    color: #03519e;
}

/*inbox button on message toolbar*/
.light-theme .rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading {
    color: #393b3d;
    background-color: #000000;
    box-shadow: inset 0 -4px 0 0 #393b3d;
}


/* likes and percentage below game card*/
.light-theme .game-card-info .info-label {
    color: #dfdfdf99;
}


/*top bar titles*/
.light-theme .rbx-header .text-header, .light-theme .rbx-header .text-header:active, .light-theme .rbx-header .text-header:focus, .light-theme .rbx-header .text-header:hover, .light-theme .rbx-header .text-header:link, .light-theme .rbx-header .text-header:visited {
    color: #90e55b;
}

/*roblox icon*/
.light-theme .icon-default-logo-r, .light-theme .icon-logo-r, .light-theme .icon-logo-r-95 {
    background-image: url(https://t5.rbxcdn.com/192e6dcbe104c5f3cf34a8ffeb77d836);
}

/*game card description text*/
.gotham-font div {
    font-size: 16px;
    font-weight: 400;
    line-height: 1.4em;
    color: white;
}


/*navigation side bar*/
#navigation {
    background-color: rgb(144, 229,91,0.8) !important;
}

/*side bar menu text*/
.gotham-font .font-bold, .gotham-font .font-bold:active, .gotham-font .font-bold:hover, .gotham-font .font-bold:link, .gotham-font .font-bold:visited, .gotham-font .font-header-2, .gotham-font .font-header-2:active, .gotham-font .font-header-2:hover, .gotham-font .font-header-2:link, .gotham-font .font-header-2:visited, .gotham-font .h5, .gotham-font .h5:active, .gotham-font .h5:hover, .gotham-font .h5:link, .gotham-font .h5:visited, .gotham-font .h6, .gotham-font .h6:active, .gotham-font .h6:hover, .gotham-font .h6:link, .gotham-font .h6:visited, .gotham-font .text-footer-nav, .gotham-font .text-footer-nav:active, .gotham-font .text-footer-nav:hover, .gotham-font .text-footer-nav:link, .gotham-font .text-footer-nav:visited, .gotham-font .text-label, .gotham-font .text-label:active, .gotham-font .text-label:hover, .gotham-font .text-label:link, .gotham-font .text-label:visited, .gotham-font .text-lead, .gotham-font .text-lead:active, .gotham-font .text-lead:hover, .gotham-font .text-lead:link, .gotham-font .text-lead:visited, .gotham-font .text-name, .gotham-font .text-name:active, .gotham-font .text-name:hover, .gotham-font .text-name:link, .gotham-font .text-name:visited, .gotham-font .text-subject, .gotham-font .text-subject:active, .gotham-font .text-subject:hover, .gotham-font .text-subject:link, .gotham-font .text-subject:visited, .gotham-font .text-warning, .gotham-font .text-warning:active, .gotham-font .text-warning:hover, .gotham-font .text-warning:link, .gotham-font .text-warning:visited, .gotham-font h5, .gotham-font h5:active, .gotham-font h5:hover, .gotham-font h5:link, .gotham-font h5:visited, .gotham-font h6, .gotham-font h6:active, .gotham-font h6:hover, .gotham-font h6:link, .gotham-font h6:visited, .gotham-font strong, .gotham-font strong:active, .gotham-font strong:hover, .gotham-font strong:link, .gotham-font strong:visited {
    font-size: 16px;
    color: #f7f7f7;
    font-weight: 500;
}


/*top bar age*/
.light-theme .text, .light-theme .text-date-hint, .light-theme .text-default, .light-theme .text-description, .light-theme .text-footer, .light-theme .text-info, .light-theme .text-label, .light-theme .text-secondary, .light-theme p {
    color: #90e65a;
}
}
