css:
@-moz-document url-prefix("https://www.postaonline.cz/") {
    .headerTop {
        padding: 8px 0;
    }

    .headerTop .col-md-6 {
        padding-top: 3px;
    }
    .headerTop .col-md-6.align-right {
        padding-top: 6px;
    }

    #parametry .container, #podani-baliku .container {
        padding-top: 10px;
    }

    .page .container {
        padding-top: 10px;
        padding-bottom: 40px;
    }

    .page h1 {
        margin-top: 0;
        margin-bottom: 10px;
        font-size: 29px;
        font-weight: 300;
        line-height: 38px;
    }

	.col-form-label {
		white-space: nowrap;
	}

	#eStitekInfoText {
		display: none;
	}

    .order .section-order {
        margin-top: 0;
    }

    .order .section-order .heading {
        display: none;
    }

    .order .section-order .heading h2 {
        display: none;
        text-align: center;
        margin-top: 5px;
        font-size: 20px;
        line-height: 20px;
        color: #002776;
    }

    .order .section-order hr {
        display: none;
    }

    .section {
        margin-bottom: 14px;
        padding: 26px 26px 10px;
        border-radius: 4px;
        border: 1px solid #ccd4e4;
    }

	.form-group {
    	margin-bottom: 5px;
    }

    #sumCenyBalikuDivHeadId + hr {
        display: none;
    }

    div.summary > p {
        display: none;
    }

    #parametry .summary .add-submit, #podani-baliku .summary .add-submit {
        padding-top: 10px;
        padding-bottom: 10px;
        text-align: center;
    }

    #celniObsahSumary {
        display: none;
    }
}
