css:
@-moz-document regexp("^((?!blog).)*https://www.luogu.com.cn((?!blog).)*$")
{

/* ------------------------------ 透明度 ------------------------------- */

/* 卡片背景透明度：0.9 */
  .lg-article , .lg-summary , .lg-article-sub , .lg-content-table-left , .card
  {
    background: rgba(252,252,252,0.9) !important;
  }
/* 引用内容透明度: 0.95*/
  blockquote
  {
    background: rgba(252,252,252,0.95) !important;
    border-radius: 12px !important;
  }
/* 全局透明度 0.95 */
  .lfe-body
  {
    opacity: 0.95;
  }
/*  评论和犇犇背景透明度：0.9   */
  .am-comment-main
  {
    background: rgba(255,255,255,0.9) !important;
  }

/* 题解底栏透明度 */
  .solution-article > .operations.float
  {
    opacity: 0.95;
  }

/* 题解区 “展开全文” 按钮（不喜欢可以注释） */

  .expand
  {
    background: rgba(255,255,255,0) !important;
  }

  .expand > .color-default
  {
    background: rgba(255,255,255,0.9) !important;
    border-radius: 5px 5px 5px 5px;
  }

/* 修复首页顶栏蹦出背景的问题 */
  .tiny > .background
  {
    background: transparent !important;
  }

/* 已知bug：有时无法点击头像下的注销按钮 */
  .tiny
  {
    z-index: 10 !important;
  }

  .user-nav
  {
    z-index: 10 !important;
  }

/* -------------------------------- 圆角化 --------------------------------- */


  #app-sidenav
  {
    background: none;
  }

  a[data-v-3e5191a6]:hover
  {
    border-radius: 10px 10px 10px 10px;
    color: #3498db;
    background: #fff;
    box-shadow: 2px 2px 2px #5e5e5e;
    transition: color 1s,background 0.45s,box-shadow 0.45s;
  }

  a[data-v-3e5191a6]
  {
    background: none;
    color: #fff0;
    color: #fff;
    background: #3498db;
    box-shadow: 2px 2px 2px #5e5e5e;
    border-radius: 10px 10px 10px 10px;
    margin: 0px 4px 4px 0px;
    filter: none !important;
  }

  i[data-v-7e88b118]
  {
    color: #fff0;
    color: #0e90d2;
    width: 4em;
    height: 3.8em;
    padding: 1.2em 0em 0em 0em;
  }

  div[data-v-7e88b118]
  {
    background-color: #fff0 !important;
  }

  i[data-v-7e88b118]:hover
  {
    color: #0e90d2;
  }

  a[data-v-7e88b118]
  {
    filter: invert(70%);
  }

  .popup-button
  {
    height: 4em;
  }

  .content
  {
    box-shadow: 0px 0px 0px #5e5e5e;
    height: 4em !important;
    background: #fff;
  }

  .bread-crumb[data-v-3c5eed85]
  {
    padding-top: 0;
  }

  .width-wrap>h1
  {
    margin: 0;
  }

  .lg-content
  {
   /*background: -webkit-linear-gradient(to right, #D6A4A4, #DAE2F8);  */
   /*background: linear-gradient(to right, #D6A4A4, #DAE2F8) !important;*/
    margin: 0px 0px 0px 0px;
    padding: 0px 0px 0px 65px;
  }

  .lg-article
  {
    border-radius: 15px 15px 15px 15px;
    box-shadow: 0px 0px 0px #5e5e5e;
  }

  .lg-summary
  {
    border-radius: 15px 15px 15px 15px;
    box-shadow: 0px 0px 0px #5e5e5e;
  }

  .am-viewport
  {
    border-radius: 5px 5px 5px 5px;
  }

  .am-btn
  {
    border-radius: 10px 10px;
  }

  .lg-toolbar
  {
    background: #fff0;
    border: none;
  }

  .am-collapse
  {
    box-shadow: 2px 2px 2px #131212d9;
  }

  .lg-header, .lg-header-list>li
  {
    background-color: white;
    color: #131212d9 !important;
  }

  .lg-header-li
  {
    color: #131212d9 !important;
  }

  .am-form-field
  {
    border-radius: 100px 100px 100px 100px;
  }

  .am-input-group-label
  {
    border-radius: 100px 100px 100px 100px;
  }

  .lg-table-bg0
  {
    background: #fff;
    border-radius: 12px 12px 12px 12px;
    box-shadow: 0px 0px 0px #5e5e5e;
  }

  .lg-table-bg1
  {
    background: #fff;
    border-radius: 12px 12px 12px 12px;
    box-shadow: 0px 0px 0px #5e5e5e;
  }

  .lg-table-bg0 .am-progress
  {
    box-shadow: none;
    border-radius: 0;
  }

  .am-pagination>li
  {
    border-radius: 20px 20px 20px 20px;
    box-shadow: 0px 0px 0px #5e5e5e;
    margin: 4px 0px 0px 4px;
  }

  .am-comment-main
  {
    border: 0px solid;
    border-radius: 20px 20px 20px 20px;
    box-shadow: 0px 0px 0px #9f9f9f;
  }

  .am-comment-main::before
  {
    display: none;
  }

  .am-comment-main::after
  {
    display: none;
  }

  .am-comment-hd
  {
    background: transparent;
    border-radius: 12px 12px 12px 12px;
  }

  .am-comment-bd
  {
    background: transparent;
    border-radius: 12px 12px 12px 12px;
  }

  .am-panel
  {
    border: 0px solid;
    border-radius: 10px 10px 10px 10px;
    box-shadow: 0px 0px 0px #9f9f9f;
    opacity: 1;
  }

  .am-panel-bd
  {
    opacity: 1;
    background: rgba(255,255,255,1);
  }

  .am-panel-primary
  {
    opacity: 1;
    background: rgba(255,255,255,1);
  }

  .am-pagination>li>a
  {
    background: rgba(255,255,255,1);
    border-radius: 50px 50px 50px 50px;
    box-shadow: 0px 0px 0px #9f9f9f;
    border: 0px solid;
    margin: 2px 2px 2px 2px;
  }

  .am-pagination>li>a:hover
  {
    background: rgba(255,255,255,1);
    border-radius: 50px 50px 50px 50px;
    box-shadow: 0px 0px 0px #9f9f9f;
    border: 0px solid;
    color: red;
  }

  .am-pagination .am-active>a
  {
    background: rgba(255,255,255,1);
    border-radius: 50px 50px 50px 50px;
    box-shadow: 0px 0px 0px #9f9f9f;
    color: #0e90d2;
    font-weight: bold;
  }

  .am-pagination .am-active>a:hover
  {
    background: rgba(255,255,255,1);
    border-radius: 50px 50px 50px 50px;
    box-shadow: 0px 0px 0px #9f9f9f;
    color: red;
  }

  .lg-content-table-left
  {
    border: 0px solid;
    border-radius: 20px 20px 20px 20px;
    box-shadow: 0px 0px 0px #9f9f9f;
  }

  .lg-index-contest
  {
    background: rgba(255,255,255,1);
  }

  .lg-sidebar-list>li
  {
    margin: 5px 5px 5px 5px;
    border-radius: 20px 20px 20px 20px;
    box-shadow: 0px 0px 0px #5e5e5e;
  }

  .lg-sidebar-list
  {
    box-shadow: none;
  }

  code
  {
    font-family: consolas;
  }

  pre
  {
    font-family: consolas;
  }

  .am-nav>li.am-active>a, .am-nav>li.am-active>a:focus, .am-nav>li.am-active>a:hover
  {
    background: none;
    font-weight: bold;
    color: #0e90d2;
  }

  .am-nav>li>a:focus, .am-nav>li>a:hover
  {
    background: none;
    color: red;
  }

  #app-footer, #app-header
  {
    margin-left: 0em;
  }

  .width-wrap>h1
  {
    color: #1b1919d6;
    font-size: 1.5em;
  }

  a:hover
  {
    color: #ff6464;
  }

  .popup[data-v-46951e14]
  {
    border: none;
  }

  .lg-record-tile
  {
    border-radius: 20px 20px 20px 20px;
    box-shadow: 0px 0px 0px #5e5e5e;
  }

  .lg-record-tile>small:first-child
  {
    text-align: center;
    padding-left: 0px;
  }

  .am-progress
  {
    box-shadow: 2px 2px 2px #5e5e5e;
    border-radius: 100px 100px 100px 100px;
  }

  .lg-left .center>img
  {
    border: none;
    border-color: #fff;
  }

  .lfe-form-sz-middle
  {
    border: 0px solid;
    border-radius: 8px !important;
    box-shadow: 0px 0px 0px #9f9f9f !important;
  }

  .copy-btn
  {
    border-radius: 5px 5px 5px 5px !important;
    box-shadow: 0px 0px 0px #9f9f9f !important;
  }

  .button-in-anchor
  {
    border: 0px solid;
    border-radius: 10px 10px 10px 10px !important;
    box-shadow: 0px 0px 0px #9f9f9f !important;
  }

  .am-form
  {
    border: 0px solid;
    border-radius: 2px 2px 2px 2px !important;
    box-shadow: 0px 0px 0px #9f9f9f !important;
  }

  .test-case
  {
    border: 0px solid;
    border-radius: 15px 15px 15px 15px !important;
    box-shadow: 0px 0px 0px #9f9f9f !important;
    opacity: 1.0 !important;
  }

  .content
  {
    background: transparent !important;
    box-shadow: 0px 0px 0px #9f9f9f !important;
    height: 100% !important;
  }

  .card
  {
    border-radius: 15px 15px 15px 15px !important;
    box-shadow: 0px 0px 0px #9f9f9f !important;
  }

  .popup
  {
    border-radius: 15px 15px 15px 15px !important;
    box-shadow: 0px 0px 0px #9f9f9f;
  }

  .user-header-top
  {
    border-radius: 15px 15px 0px 0px !important;
  }

  .lfe-form-sz-small
  {
    border-radius: 10px !important;
    box-shadow: 0px 0px 0px #9f9f9f;
  }

  .solution-article > .operations.float
  {
    border-radius: 10px !important;
    box-shadow: 0px 0px 0px #9f9f9f;
  }

  .expand
  {
    border-radius: 10px !important;
    box-shadow: 0px 0px 0px #9f9f9f;
    opacity: 1.0 !important;
  }

  .drop
  {
    border-radius: 10px !important;
    box-shadow: 0px 0px 0px #9f9f9f;
  }

  .am-form-field
  {
    border-radius: 10px !important;
    box-shadow: 0px 0px 0px #9f9f9f;
  }

  .frame
  {
    border-radius: 10px !important;
    box-shadow: 0px 0px 0px #9f9f9f;
  }

  .refined-input
  {
    border-radius: 10px !important;
    box-shadow: 0px 0px 0px #9f9f9f;
  }

  .refined-input .input-wrap .frame
  {
    border-radius: 10px !important;
    box-shadow: 0px 0px 0px #9f9f9f;
  }

  .input-wrap > input
  {
    border-radius: 10px !important;
    box-shadow: 0px 0px 0px #9f9f9f;
  }

  .center
  {
    border-radius: 10px !important;
    box-shadow: 0px 0px 0px #9f9f9f;
  }

  ::-webkit-scrollbar
  {
    border-radius: 5px !important;
  }

  ::-webkit-scrollbar-track
  {
    border-radius: 5px !important;
  }

  ::-webkit-scrollbar-thumb
  {
    border-radius: 5px !important;
  }

  ::-webkit-scrollbar-thumb:hover
  {
    border-radius: 5px !important;
  }

  code
  {
    border-radius: 5px !important;
    box-shadow: 0px 0px 0px #9f9f9f;
  }

  pre
  {
    border-radius: 12px !important;
    box-shadow: 0px 0px 0px #9f9f9f;
  }

  textarea
  {
    border-radius: 12px !important;
    box-shadow: 0px 0px 0px #9f9f9f;
  }

  span
  {
    border-radius: 5px !important;
    box-shadow: 0px 0px 0px #9f9f9f;
  }
}
