css:
@-moz-document domain("imdb.com") {
.jw-slider-time .jw-progress {
	background: linear-gradient(90deg, rgba(248,13,27,1) 0%, rgba(255,160,0,1) 20%, rgba(255,227,2,1) 40%, rgba(0,250,34,1) 60%, rgba(0,169,238,1) 80%, rgba(159,0,255,1) 100%) !important;
	height: 12px !important;
	border: none !important;
}

.jw-slider-vertical .jw-progress {
	background: linear-gradient(0deg, rgba(248,13,27,1) 0%, rgba(255,160,0,1) 20%, rgba(255,227,2,1) 40%, rgba(0,250,34,1) 60%, rgba(0,169,238,1) 80%, rgba(159,0,255,1) 100%) !important;
	width: 6px !important;
}

.jw-knob {
	background-color: rgba(159,0,255,1) !important;
	height: 20px !important;
	width: 20px !important;
}
}
