@document url-prefix("https://3dmm.com"){
.alt1 {
    background-color: #3b3b3b;
    color:white;
}
.alt1 a:link, .alt1Active a:link, .alt1 a:visited {
    color: #afbade;
    text-decoration: none;
}
.tcat {
    background: #D2D2D2 url(https://i.imgur.com/EBoEr2L.png) repeat-x top left;
    color: #48474C;
    font: bold 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
.alt1, .alt1Active {
    background: #3b3b3b;
    color: #cfcfcf;
}
.alt2, .alt2Active {
    background: #474747;
    color: #cfcfcf;
}
.tborder {
    background: #212121;
    border: 1px solid #48474C;
}
.time {
    color: #cccccc;
}
.alt2 a:link, .alt2Active a:link {
    color: #cfd4e8;
    text-decoration: none;
}
