/* vBulletin 3 CSS For Style '3dmm Liquid (Halloween)' (styleid: 3) */
@document url-prefix("https://3dmm.com"){
body{
background: #000000;
	color: #464646;
	font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
	margin: 10px 10px 10px 10px;
	padding: 0px;
	background-image: url("https://3dmm.com/images/halloween/background.jpg");
}
a:link
{
	color: #464646;
	text-decoration: none;
}
a:visited
{
	color: #464646;
	text-decoration: none;
}
a:hover, a:active
{
	color: #464646;
	text-decoration: underline;
}
.page
{
	background: #979C86;
	color: #FFFFFF;
}
.page a:link
{
	color: #FFFFFF;
}
.page a:visited
{
	color: #FFFFFF;
}
.page a:hover, .page a:active
{
	color: #FFFFFF;
}
td, th, p, li
{
	font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
.tborder
{
	background: #DCDCDC;
	border: 1px solid #48474C;
}
.tcat
{
	background: #D2D2D2 url(http://www.3dmm.com/images/gradiant.png) repeat-x top left;
	color: #464646;
	font: bold 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
.tcat a:link
{
	color: #464646;
	text-decoration: none;
}
.tcat a:visited
{
	color: #464646;
	text-decoration: none;
}
.tcat a:hover, .tcat a:active
{
	color: #464646;
	text-decoration: underline;
}
.thead
{
	background: #464646;
	color: #FFFFFF;
	font: bold 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
.thead a:link
{
	color: #FFFFFF;
}
.thead a:visited
{
	color: #FFFFFF;
}
.thead a:hover, .thead a:active
{
	color: #FFFFFF;
	text-decoration: underline;
}
.tfoot
{
	background: #979C86 !important;
	color: #E6E6E6;
	text-align: center;
}
.tfoot a:link
{
	color: #E6E6E6;
}
.tfoot a:visited
{
	color: #E6E6E6;
}
.tfoot a:hover, .tfoot a:active
{
	color: #FFFFFF;
}
.alt1, .alt1Active
{
	background: #F0F0F0;
	color: #27222E;
}
.alt1 a:link, .alt1Active a:link
{
	color: #464646;
	text-decoration: none;
}
.alt1 a:visited, .alt1Active a:visited
{
	color: #464646;
	text-decoration: none;
}
.alt1 a:hover, .alt1 a:active, .alt1Active a:hover, .alt1Active a:active
{
	color: #27222E;
	text-decoration: underline;
}
.alt2, .alt2Active
{
	background: #E6E6E6;
	color: #27222E;
}
.alt2 a:link, .alt2Active a:link
{
	color: #464646;
	text-decoration: none;
}
.alt2 a:visited, .alt2Active a:visited
{
	color: #464646;
	text-decoration: none;
}
.alt2 a:hover, .alt2 a:active, .alt2Active a:hover, .alt2Active a:active
