	color: #464646;
	padding: 10px;
	border: 2px outset;
}
.panel a:link
{
	color: #464646;
	text-decoration: none;
}
.panel a:visited
{
	color: #464646;
	text-decoration: none;
}
.panel a:hover, .panel a:active
{
	color: #464646;
	text-decoration: underline;
}
.panelsurround
{
	background: #E6E6E6;
	color: #575757;
}
legend
{
	color: #464646;
	font: 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
.vbmenu_control
{
	background: #464646;
	color: #F0F0F0;
	font: bold 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
	padding: 3px 6px 3px 6px;
	white-space: nowrap;
}
.vbmenu_control a:link
{
	color: #E6E6E6;
	text-decoration: none;
}
.vbmenu_control a:visited
{
	color: #E6E6E6;
	text-decoration: none;
}
.vbmenu_control a:hover, .vbmenu_control a:active
{
	color: #E6E6E6;
	text-decoration: underline;
}
.vbmenu_popup
{
	background: #FFFFFF;
	color: #464646;
	border: 1px solid #48474C;
}
.vbmenu_option
{
	background: #C1C0C7;
	color: #464646;
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
	white-space: nowrap;
	cursor: pointer;
}
.vbmenu_option a:link
{
	color: #464646;
	text-decoration: none;
}
.vbmenu_option a:visited
{
	color: #464646;
	text-decoration: none;
}
.vbmenu_option a:hover, .vbmenu_option a:active
{
	color: #464646;
	text-decoration: none;
}
.vbmenu_hilite
{
	background: #F0F0F0;
	color: #464646;
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
	white-space: nowrap;
	cursor: pointer;
}
.vbmenu_hilite a:link
{
	color: #464646;
	text-decoration: none;
}
.vbmenu_hilite a:visited
{
	color: #464646;
	text-decoration: none;
}
.vbmenu_hilite a:hover, .vbmenu_hilite a:active
{
	color: #464646;
	text-decoration: none;
}
/* ***** styling for 'big' usernames on postbit etc. ***** */
.bigusername { font-size: 14pt; }

/* ***** small padding on 'thead' elements ***** */
td.thead, div.thead { padding: 4px; }

/* ***** basic styles for multi-page nav elements */
.pagenav a { text-decoration: none; }
.pagenav td { padding: 2px 4px 2px 4px; }

/* ***** define margin and font-size for elements inside panels ***** */
.fieldset { margin-bottom: 6px; }
.fieldset, .fieldset td, .fieldset p, .fieldset li { font-size: 11px; }

/* ***** don't change the following ***** */
form { display: inline; }
label { cursor: default; }
.normal { font-weight: normal; }
.inlineimg { vertical-align: middle; }
.threedmm_menu_item { text-align:center; vertical-align:middle; width:11%; }
.threedmm_menu_image { margin:2px; border:0px; }

.threedmm_border_top { background: url('http://www.3dmm.com/images/halloween/border_top.gif') repeat-x top; }
.threedmm_border_left { background: url('http://www.3dmm.com/images/halloween/border_left.gif') #979C86 repeat-y top left; padding-left:25px; }
.threedmm_border_right { background: url('http://www.3dmm.com/images/halloween/border_right.gif') #979C86 repeat-y top right; padding-right:25px; }
.threedmm_border_bottom { background: url('http://www.3dmm.com/images/halloween/border_bottom.gif') repeat-x bottom; }

.threedmm_corner_top_left { background: url('http://www.3dmm.com/images/halloween/border_top_left.gif') no-repeat top left; height:100px; width:60px; }
.threedmm_corner_top_right { background: url('http://www.3dmm.com/images/halloween/border_top_right.gif') no-repeat top right; height:100px; width:60px; }
.threedmm_corner_bottom_left { background: url('http://www.3dmm.com/images/halloween/border_bottom_left.gif') no-repeat bottom left; height:100px; width:60px; }
.threedmm_corner_bottom_right { background: url('http://www.3dmm.com/images/halloween/border_bottom_right.gif') no-repeat bottom right; height:100px; width:60px; }

.threedmm_postbit_message a:link { font-weight:bold; text-decoration:underline; }
.threedmm_postbit_message a:visited { font-weight:bold; text-decoration:underline; }
.threedmm_postbit_message a:hover { font-weight:bold; text-decoration:none; }

