css:
@-moz-document domain("w3schools.com") {
.w3-bar, .ws-table-all tr:nth-child(2n), .tut_overview, .active_overview, .ref_overview, .activesub {background-color: #18181A !important;}
.w3-white, .w3-code {background-color: #2B2D31 !important;}
#leftmenuinnerinner, #topTD, #tryitLeaderboard, .w3-info, .w3-example, .ws-table-all tr:nth-child(2n+1), .exercisemenu, .exercisemenu * {background-color: #232327 !important;}
#w3-exerciseform {background-color: #04AA6D !important;}
.exercisewindow, .exerciseprecontainer, #assignmentcontainer, input {background-color: #00000052 !important;}
.w3-note, .w3-codespan {background-color: #ffff0008 !important;}


.w3-bar, .w3-white, #leftmenuinnerinner, #w3-exerciseform *, #topTD, .exercisemenu *, .active_overview, .activesub, input {color: #fff !important;}


.ws-table-all, .ws-table-all tr {border-color: 1px #40444B !important;}
#assignmentcontainer {outline: 10px solid #40444b !important;}

hr, #footer, .ws-table-all {border-top: 1px solid #40444b !important;}
.ws-table-all, .ws-table-all tr {border-bottom: 1px solid #40444b !important;}
.ws-table-all {border-left: 1px solid #40444b !important;}
#main, .ws-table-all {border-right: 1px solid #40444b !important;}

body::-webkit-scrollbar, #leftmenuinnerinner::-webkit-scrollbar  {
  width: 10px;               /* width of the entire scrollbar */
}

body::-webkit-scrollbar-track, #leftmenuinnerinner::-webkit-scrollbar-track {
  background: #18181A;        /* color of the tracking area */
}

body::-webkit-scrollbar-thumb, #leftmenuinnerinner::-webkit-scrollbar-thumb {
  background-color: #0a0a0a;    /* color of the scroll thumb */
  border-radius: 20px;       /* roundness of the scroll thumb */
  border: 1px solid #18181A;  /* creates padding around scroll thumb */
}


body {
  scrollbar-width: thin;          /* "auto" or "thin" */
  scrollbar-color: #0a0a0a #18181A;   /* scroll thumb and track */
}
}
