css:
@-moz-document url-prefix("https://www.roblox.com/develop"), url-prefix("https://www.roblox.com/universes"), url-prefix("https://www.roblox.com/places") {
#BodyWrapper,
.social-links-container .add-button {
    background: #232527;
}
.tab-content,
.tab-container + div > div {
    background-color: #232527;
    border-top: #232527;
}
.left-nav-menu ul#genresUl {
    color: #bdbebe
}
div#catalog .selected {
    color: #FFFEFE;
}
body,
.text,
pre {
    color: #FFFEFE;
    background-color: #232527;
}
#catalog input.Watermark {
    color: #626565;
    background-color: rgba(0, 0, 0, .7);
}
.text-box.text-box-small {
    border: 1px solid #bdbebe;
}
.text-box.text-box-small {
    border: 1px solid #bdbebe;
    color: #bdbebe;
    background-color: rgba(0, 0, 0, .7);
}
#Body.body-width {
    background-color: #232527;
}
h2,
h2 a:link,
h2 a:visited,
h2 a:active,
h2 .text {
    color: #FFFEFE!important;
}
a:link,
a:visited,
a:active,
a:link,
::-webkit-scrollbar-thumb,
a:focus,
a:hover {
    color: #0074FE;
}
.selectedAssetTypeFilter {
    background: #4D4E50;
}
div#catalog div#dropdown ul#dropdownUl {
    border: 1px solid #bdbebe;
    background: #383B3C;
}
div#catalog div#dropdown ul li a,
div#catalog div#dropdown ul li span {
    color: hsla(0, 0%, 100%, .7);
}
div#catalog div#dropdown ul#dropdownUl {
    border: 1px solid #bdbebe;
    background: #383B3C;
}
.CatalogItemInner,
.CatalogItemInner:hover,
#build-page .gear-button-wrapper {
    background: #232527!important;
    border-color: #232527!important;
}
.HoverInfo,
.CatalogItemInner:hover .HoverInfo {
    color: #bdbebe;
}
.CatalogItemInfoLabel,
.CatalogItemInner:hover .CatalogItemInfoLabel,
.popover-content .dropdown-menu li a {
    color: #bdbebe;
}
.CatalogItemInner span.NotAPrice {
    color: #bdbebe;
}
.SmallCatalogItemView {
    background: #232527;
}
img.original-image {
    border: 1px solid #bdbebe;
}
.SmallCatalogItemView:hover .CatalogItemInner {
    border: 1px solid #bdbebe;
}
.divider-right {
    border-right: 1px solid #bdbebe;
}
#categoriesForKeyword {
    border: none;
    background-color: rgba(0, 0, 0, .7);
    color: hsla(0, 0%, 100%, .7);
}
#catalog #SortOptions select {
    border: 1px solid #bdbebe;
}
select {
    color: hsla(0, 0%, 100%, .7);
    background-color: rgba(0, 0, 0, .7);
    border: 1px solid #bdbebe;
}
.redesign .tab_white_31h_container .ajax__tab_active .ajax__tab_inner,
.tab.active,
.tab-container > .tab-active,
.tab-container > .tab-active:hover {
    background-color: #232527;
    border-color: #bdbebe;
}
.redesign .tab_white_31h_container .ajax__tab_inner,
.tab,
.tab-container > div {
    border: 1px solid #bdbebe;
}
.redesign .tab_white_31h_container .ajax__tab_inner,
.tab,
.tab-container > div {
    background-color: #4D4E50;
}
.redesign .tab_white_31h_container .ajax__tab_hover .ajax__tab_inner,
.tab:hover,
.tab-container > div:hover {
    background-color: #383B3C;
    cursor: pointer;
}
#categoriesForKeyword {
    background-image: url(https://i.ibb.co/dcQ6xfZ/Roblox-Arrow-maybe.png);
}
.library-autocomplete-items div {
    background-color: #383B3C;
    border-bottom: 1px solid #bdbebe;
}
.library-autocomplete-items div:hover,
.dropdown-menu li:hover {
    background-color: #4D4E50;
}

.library-autocomplete-items {
    border: 1px solid #bdbebe;
}
.container-footer {
    background-color: #232527;
}
.input-group-btn .input-dropdown-btn {
    background-color: transparent;
    border: 1px solid #bdbebe;
    color: hsla(0, 0%, 100%, .7);
}
.text-footer-nav:focus,
.text-footer-nav:hover {
    color: #fff;
}
.icon-checkmark-16x16,
.icon-close-16x16,
.icon-close-gray-16x16,
.icon-default-generic-16x16,
.icon-down-16x16,
.icon-edit-16x16,
.icon-left-16x16,
.icon-moreinfo-16x16,
.icon-red-timer-16x16,
.icon-right-16x16,
.icon-settings-16x16,
.icon-up-16x16,
.input-group-btn .input-dropdown-btn:focus .icon-down,
.input-group-btn .input-dropdown-btn:hover .icon-down,
.open .input-dropdown-btn .icon-down,
.rbx-select-group:hover .icon-down {
    background-image: url(https://images.rbxcdn.com/a057a8bc94e7ab78517765ddb4e77384-generic_dark_11062018.svg);
}
.icon-additem,
.icon-arrow-right-blue,
.icon-audio,
.icon-back,
.icon-bigplay,
.icon-bigplay-fill,
.icon-bigstop,
.icon-bigstop-fill,
.icon-brokenpage,
.icon-carousel-left,
.icon-carousel-right,
.icon-chat-gray,
.icon-check-selection,
.icon-checkmark,
.icon-checkmark-blue,
.icon-checkmark-on,
.icon-checkmark-white,
.icon-checkmark-white-bold,
.icon-clock,
.icon-close,
.icon-close-white,
.icon-copy,
.icon-default-generic,
.icon-down,
.icon-edit,
.icon-female,
.icon-female-white,
.icon-first-gray,
.icon-first-page,
.icon-games-carousel-left,
.icon-games-carousel-left.hover,
.icon-games-carousel-right,
.icon-games-carousel-right.hover,
.icon-globe,
.icon-grid,
.icon-home,
.icon-inactive,
.icon-last-gray,
.icon-last-page,
.icon-left,
.icon-left-gray,
.icon-locked,
.icon-logo-apple,
.icon-logo-win,
.icon-male,
.icon-male-white,
.icon-menu,
.icon-menu-black,
.icon-minus,
.icon-more,
.icon-more-gray-vertical,
.icon-moreinfo,
.icon-next,
.icon-notifications,
.icon-page,
.icon-password-hide,
.icon-password-hide-white,
.icon-password-show,
.icon-password-show-white,
.icon-pastname,
.icon-pause,
.icon-pause-fill,
.icon-play,
.icon-playgame,
.icon-plus,
.icon-regenerate,
.icon-right,
.icon-right-gray,
.icon-sandglass,
.icon-search,
.icon-settings,
.icon-slideshow,
.icon-sorting,
.icon-trash-bin,
.icon-unlocked,
.icon-up,
.icon-viewdetails {
    background-image: url(https://images.rbxcdn.com/a057a8bc94e7ab78517765ddb4e77384-generic_dark_11062018.svg);
}
#build-page td.menu-area a.tab-item-selected {
    border-top: 1px solid #bdbebe;
    border-bottom: 1px solid #bdbebe;
    border-left: 1px solid #bdbebe;
    background-color: #383B3C;
}
#build-page td.menu-area a.tab-item:hover {
    text-decoration: none;
    background-color: #383B3C;
}
#build-page td.menu-area a.tab-item {
    color: hsla(0, 0%, 100%, .7)!important;
}
h3,
h3 a,
#build-dropdown-menu a {
    color: #FFFEFE;
}
.widget-name {
    background-color: #383B3C;
}
.copyright-container {
    border-top: 1px solid #656668;
}
.assetTypeFilter a {
    background-color: #4D4E50;
}
.item-table .btr-listed-status {
    color: hsla(0, 0%, 100%, .7);
}
#build-page table.section-header label.checkbox-label {
    float: initial;
    color: #fff;
    font-size: 12px;
}
.left-nav-menu ul {
    border-bottom: 1px solid #bdbebe;
}
.dropdownUl li:hover {
    background-color: #4D4E50;
}
.CatalogItemName a {
    background-color: transparent
}
.item-image-wrapper a,
img {
    background-color: transparent
}
.footer-link a {
    background-color: transparent
}
.dark-theme #nav-blog:hover,
.dark-theme #nav-character:hover,
.dark-theme #nav-friends:hover,
.dark-theme #nav-group:hover,
.dark-theme #nav-home:hover,
.dark-theme #nav-inventory:hover,
.dark-theme #nav-message:hover,
.dark-theme #nav-my-feed:hover,
.dark-theme #nav-profile:hover,
.dark-theme #nav-shop:hover,
.dark-theme #nav-trade:hover {
    background-color: transparent;
}
.light-theme #nav-blog:hover,
.light-theme #nav-character:hover,
.light-theme #nav-friends:hover,
.light-theme #nav-group:hover,
.light-theme #nav-home:hover,
.light-theme #nav-inventory:hover,
.light-theme #nav-message:hover,
.light-theme #nav-my-feed:hover,
.light-theme #nav-profile:hover,
.light-theme #nav-shop:hover,
.light-theme #nav-trade:hover {
    background-color: transparent;
}
.dark-theme .rbx-left-col li .text-nav:active,
.dark-theme .rbx-left-col li .text-nav:link,
.dark-theme .rbx-left-col li .text-nav:visited {
    color: #fff;
    background-color: transparent;
}
.light-theme .rbx-left-col li .text-nav:active,
.light-theme .rbx-left-col li .text-nav:link,
.light-theme .rbx-left-col li .text-nav:visited {
    color: #fff;
    background-color: transparent;
}
#build-page .gear-button-wrapper {
    background-color: #232527;
    border-color: #232527;
}
#build-page td.menu-area a.tab-item:hover {
    color: #FFFEFE!important;
}
.menu-area divider-right a {
    color: #FFFEFE!important;
}
.robux-text {
    color: #00B200;
    font-weight: bold;
}
html {
    background-color: #232527;
}
#build-page .item-table .totals-label span {
    color: #FFFEFE;
}
#name {
    border: 1px solid #bdbebe;
    color: #bdbebe;
    background-color: rgba(0, 0, 0, .7);
}
.navbar-header a {
    background-color: transparent;
}
.activate-cell td,
a {
    background-color: transparent;
}

.universe-name-col a {
    background-color: transparent;
}
#build-page .item-table .name-col a.title {
    background-color: transparent;
}
.modal-dialog .modal-content {
    background-color: #232527;
}
h1.header-text {
    color: #FFFEFE;
}
a.studio-launch {
    background-color: transparent;
}
li:active {
    background-color: transparent;
}
div#catalog div#dropdown ul li a:hover {
    background-color: #4D4E50;
    color: #fff;
}
#container label {
    color: #bdbebe;
}
input[type=file],
div.form-row,
.form-label {
    color: #bdbebe;
}
input#name.text-box.text-box-medium {
    background-color: rgba(0, 0, 0, .7);
    border: 1px solid #bdbebe;
    color: #bdbebe;
}
.textarea-container #description,
.text-box.text-box-medium,
#placeForm .text-box,
#placeForm .headline-banner,
.btr-versionHistory .version-card {
    background-color: rgba(0, 0, 0, .7);
    border: 1px solid #bdbebe;
    color: #bdbebe;
}
div#build-dropdown-menu,
.verticaltab.selected,
.missing-start-place {
    background-color: #383B3C;
    color: #fff!important;
}
#build-dropdown-menu a:hover,
.verticaltab a:hover,
.verticaltab a:active,
.verticaltab a:focus {
    background-color: #4D4E50!important;
}
.verticaltab:hover {
    background: #4D4E50;
    color: #fff!important;
}
.verticaltab.selected,
.social-links-container .add-button,
#build-page td.menu-area a.tab-item-selected {
    color: #fff!important;
}
.popover.bottom > .arrow:after,
.popover.bottom > .arrow,
div.arrow {
    border-bottom-color: #383B3C!important;
}
.popover-content .dropdown-menu {
    background-color: #383B3C;
    border-color: #383B3C!important;
}
#DevExTabLink a {
    color: #fff!important;
}
.separator {
    background-color: #bdbebe!important;
}
}
