css:
@-moz-document domain('youtube.com') {
ytd-thumbnail-overlay-playback-status-renderer.style-scope.ytd-thumbnail {
    position: initial;
}

yt-formatted-string.style-scope.ytd-thumbnail-overlay-playback-status-renderer {
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0;
    left: -4px;
    padding-left: 70px;
    background: transparent;
    z-index: 2;
}

ytd-thumbnail-overlay-resume-playback-renderer.style-scope.ytd-thumbnail {
    height: 100%;
    opacity: 0.8;
    background: black;  
}

ytd-thumbnail-overlay-resume-playback-renderer.style-scope.ytd-thumbnail::after {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    background: black;
    opacity: 0.8;
}

#progress.ytd-thumbnail-overlay-resume-playback-renderer {
    position: absolute;
    height: 3px;
    z-index: 1;
}
}
