@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://www.google.com/ig) {
/* UNIVERSAL BAR */
    #guser { 
        position: absolute !important; 
        top: 3px !important; 
        right: 0px !important; 
        background: transparent !important;
        visibility: hidden !important;}
    #gbar { 
        position: absolute !important; 
        top: 3px !important; 
        left: 0px;}
    #gbar a, #gbar u, #guser a, #guser b { 
        text-decoration: none !important;
        visibility: visible !important;}
    #gbarc { 
        height: 25px !important; 
        -moz-opacity: 0.70 !important;}
    .gbard { 
        position: absolute !important; 
        top: 21px !important;
        -moz-opacity: 0.55 !important;}
    #gbar a:hover, #guser a:hover {
        color: #000055 !important;}
    #gbardd a:hover {
        color: #ffffff !important; 
        background-color: #000044 !important;
        -moz-border-radius: 0px 0px 1em 1em !important;}
    #gbardd {
        -moz-border-radius: 0px 0px 1em 1em !important;}
/* INPUT AREA */
    #gsea { 
        position: absolute !important; 
        top: 50px !important;}
    input#q {
        text-align: center !important;
        padding: 3px 2px 2px 2px !important;
        width: 346px !important;
        height: 15px !important;
        border: 1px #9a9a9a solid !important;
        color: #000000 !important;
        font-weight: bold !important;
        -moz-opacity: 0.5 !important;
        -moz-border-radius: 0em 0em 1em 1em !important;}
    input#btnG { 
        position: relative !important;
        top: -6px !important;
        padding: 0px 6px 1px 6px !important;
        font-weight: 600 !important;
        border: 1px #9a9a9a solid !important;
        border-top: 0px !important;
        -moz-opacity: 0.5 !important;
        -moz-border-radius: 0em 0em 1em 1em !important;}
    input#btnI {
        display: none !important;}
    div#regular_logo, p.gseaopt a, span#more, div#featuretabs a {
        border: 0 !important;
        -moz-opacity: 0.75 !important;}
    p.gseaopt a:hover, div#featuretabs a:hover {
        text-decoration: none !important;
        -moz-opacity: 1.0 !important;}
/* TAB AREA */
    .new {
        visibility: hidden !important;}
    .unselectedtab, .unselectedtab_l, .unselectedtab_r, .spacer, .addtab, #addstuff a {
        -moz-opacity: 0.50 !important;}
    .unselectedtab:hover, #addstuff a:hover {
        -moz-opacity: 1.0 !important;
        text-decoration: none !important;
        color: #000000 !important;}
    #addstuff {
        visibility: hidden !important;}
    #addstuff a {
        visibility: visible !important;
        position: relative !important; 
        bottom: -4px !important;
        color: #000000 !important;
        border: 1px #9a9a9a solid !important;
        background-color: #ffffff !important;
        -moz-border-radius: 1em 1em 0em 0em !important;
        padding: 1px 6px 0px 6px !important;
        cursor: default !important;}
    #addstuff a:hover {
        -moz-opacity: 1.0 !important;}
    .tab {
        border-bottom-width: 0px !important;
        -moz-border-radius: 1em 1em 0em 0em !important;}
    /* #tabs {
        height: 23px !important;} */
/* CONTENT AREA */
    .modboxin {
        padding-right: 5px !important;}
    .uftl {
        white-space: nowrap !important;
        overflow: hidden !important;}
    .uftl a, .mc a, .mtlink, .modboxin_s a {
        text-decoration: none !important;}
    .skinth:hover {
        background-color: #e0e0e0 !important;}
    .ddbox, .minbox, .maxbox, .delbox, .skinthsel, .fmaxbox, .fminbox {
        cursor: default !important;}
/* FOOTER */
    #footer_promos {
        -moz-opacity: 0.30 !important;}
    #footer_promos:hover {
        -moz-opacity: 0.80 !important;}
    #footer_promos a {
        text-decoration: none !important;}
}
