@-moz-document url-prefix("https://mail.uni-leipzig.de/portal/imp/dynamic.php?page=compose") {
:root {
    --hintergrund: hsl(0, 0%, 10%);
    --link: #1ea7e6;
    --text: hsl(213, 7%, 86%);
    --text_gray: hsl(213, 1%, 72%);
    --tool_leiste: #0a0a0a;
    --kopf: #262626;
    --input: #363636;
}
html {
    background: var(--hintergrund);
}
* {
    color: var(--text);
}
a {
    color: var(--link);
}
body {
    font-size: 90%;
    background: var(--hintergrund);
}
#horde-body {
    border: none;
}
#msgSplitPane {
    border: none;
}
#composeMessage {
    background-color: var(--hintergrund);
    color: var(--text);
    outline: none;
}
.msgwrite {
    background-color: var(--kopf);
    border: none;
}
.composeAddrbook, #togglebcc, #togglecc {
    color: var(--link)
}
.hordeACBox, .impACBox, .impACBoxFocus, .hordeACBox, #subject {
    color: var(--text);
    background-color: var(--input);
    padding: 3px;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    border: none;
}
.impACBoxFocus {
    outline: auto;
}
.hordeACTrigger, .impACTrigger {
    color: var(--text);
}
#compose_upload_add {
    color: var(--link);
}
.dimpOptions a, .dimpOptions a:visited {
    color: var(--text);
}
.horde-buttonbar, .horde-buttonbar a, .horde-buttonbar a:link, .horde-buttonbar a:visited, .horde-buttonbar a:active {
    color: var(--text_gray);
    background-color: var(--tool_leiste);
}
.horde-buttonbar {
    background-image: none;
}
.horde-buttonbar li.horde-icon {
    background-image: none;
    border: none;
}
.msgwrite tr td {
    height: 30px;
}
}

