css:
/* Desktop */
@-moz-document domain('www.youtube.com') {
  #secondary { display: none; }
  #primary { width: 100%; }
  #primary,
  ytd-watch-flexy #primary.ytd-watch-flexy,
  [flexy] { 
      max-width: none; 
  }
  .html5-endscreen {display:none;}
  .ytp-pause-overlay { display:none; }
  [data-content-type="related"] { display:none; }
  #sections ytd-guide-section-renderer:nth-of-type(2n) { display:none; }
}

/* Mobile */
@-moz-document domain('m.youtube.com') {
  [data-content-type="related"] { display:none; }
  .player-container { max-width:800px; }
  /* Privacy feature, to allow hiding video by scrolling down when necessary: */
  ytm-single-column-watch-next-results-renderer   { margin-bottom: 100em; }
}
