css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://apps.facebook.com/inthemafia"),url-prefix("http://mwfb.zynga.com/"),url-prefix("http://zbar.zynga.com/") {

/* general stuff to remove ads, widen interface etc */

/*#app10979261223_iframe_canvas {width:1100px !important;margin:-60px 0 0 0;}
#index {margin:0 !important;padding:0 !important}*/

button,#pagefooter,#sidebar_ads,.fb_email_prof_header, .UIStandardFrame_SidebarAds,.zbar_main_cont {display:none !important}
#app_content_10979261223 {top:-20px !important; }
.app { width: 1100px !important; }
.UIStandardFrame_Container {top:-20px !important;width:1100px !important;background-color:black !important; float:left !important}
.UIStandardFrame_Content,#mw_masthead,#app10979261223_mw_masthead {width:1000px !important}
body {background-color:black !important}
#app10979261223_mainDiv {border: 1px white solid !important}
#app10979261223_app_layout {float:left !important; clear:both !important}

/* specific to improved fight interface */

div.fightbar {margin-top:-43px !important;border:none !important}
#user_nav,#app10979261223_user_nav {display:none !important; }
#mw_navigation,#app10979261223_mw_navigation {width:100px !important;position:absolute !important; left:810px; top:90px}

/* Fight Navigation */

.fightres_characters, .fightres_bonus_image, span.fightbar_popup  {display:none !important}
.fightbar {padding-top:50px !important}
div.fightbar_text {float:left !important; font-size:.9em !important;}
div.fightbar_skill {width:200px !important}
div.fightbar_group {width:370px !important}
div.fightbar_group_stat {float:right !important;margin:-17px 18px 0 0 !important;}
div.fightbar_top3_def {padding-left:50px !important}
div.title {display:none !important}

/* fight results */

.fight_results {position:absolute !important;width:473px !important; font-size:.8em !important; top:260px !important;left: 525px!important; ;padding:0 !important}
.fightres_title {font-size:1em !important; clear:none !important;float:left !important; margin-right:20px}
.fightres_item_link {margin:0 !important; clear:none !important}
a.sexy_button {font-size:.8em !important}
.fightres_bonus_message {width:490px !important;}
.fightres_bonuses {height:auto !important}
.fightres_bonus_image {display:none !important}
.fightres_bonus {clear:left !important}
#fightres_item_lists,#app10979261223_fightres_item_lists {position:relative !important; overflow:scroll !important;height:200px !important}

/* opponent list */

table.main_table {width:550px !important;float:left !important; clear:none !important}
}
table.main_table td {height:5px !important; }
.sexy_fight {overflow:hidden !important}
.sexy_button {height:10px !important;border: none !important}

/* removes useless info about fighting */

div[style="margin: 10px 0pt;"],ul.tabs {display:none !important}
