/* ==UserStyle==
@name         Dark Blitzstars [Use PALE theme]
@version      20211014.15.54
@namespace    https://userstyles.org/users/1266076
@description  Makes Blitzstars dark themed, somewhat.

You must use "Pale" theme as base, it does not work with "Default".
@author       hookens
@license      CC BY 4.0
==/UserStyle== */

@-moz-document domain("blitzstars.com") {
body,
html {
    color: #ddd;
}

.black-and-white .clan-page,
.black-and-white .first-row,
.black-and-white .player-stats-history,
.black-and-white .playerstats-extra,
.black-and-white .playerstats-header,
.black-and-white .playerstats-super,
.black-and-white .tanks-extra,
.black-and-white .tanks-row,
.black-and-white .top-clans-page,
.black-and-white .top-players {
    color: #DDD;
    background-color: #222;
}

.black-and-white .player-hof {
    background-color: #222;
    color: #DDD;
}

.black-and-white .player-stats-section-grid-container {
    background-color: #161616;
}

.c3 svg {
    fill: #DDD;
}

.chart-card {
    color: #DDD;
    background: #111;
}

.c3-tooltip-container {
    color: #111;
}

.h1,
.h2,
.h3,
.h4,
.h5,
.h6,
h1,
h2,
h3,
h4,
h5,
h6 {
    color: #FFF;
}

.black-and-white .playerstats-header .show-hide-text-button.achievements {
    background-color: #366588;
    color: #fff;
}

.subtitle {
    color: #fff;
}

.black-and-white .btn-danger {
    background-color: rgba(228, 7, 0, .5);
    color: #DDD;
}

.table-bordered > tbody > tr > td,
.table-bordered > tbody > tr > th,
.table-bordered > tfoot > tr > td,
.table-bordered > tfoot > tr > th,
.table-bordered > thead > tr > td,
.table-bordered > thead > tr > th {
    border: 1px solid #555;
}

.black-and-white .tanks-extra tr.tank-averages td,
.black-and-white .top-players .menu-item a:hover {
    background-color: #444;
}

.signature-block,
.black-and-white .magical-tank-view .main-window .top-bar,
.black-and-white .playerstats-super h2,
.black-and-white table.tankTable--JWOoW,
.black-and-white .clan-page .history-selection a.active,
.black-and-white .top-players .menu-item a.active,
.statColWrap--13ktq:first-of-type {
    background-color: #333;
}

.black-and-white thead th,
.new-header .header-second-bar,
.main-content-wrap,
.black-and-white .playerstats-super .clan-info-dropdown,
.player-name-history-wrapper,
.black-and-white .clan-page .clan-main,
.clan-stars {
    background-color: #222;
}

.dropdown-menu,
.black-and-white .playerstats-super .clan-info-dropdown .clan-description {
    background-color: #111;
}

.signature-block input {
    color: #BBB;
    background-color: #252525;
}

.playerstats-page {
    color: #BBB;
}


.black-and-white .table-responsive table.table thead,
.black-and-white .table-responsive table.table thead tr {
    background-color: #333!important;
}

.black-and-white .btn {
    color: #BBB;
    background-color: #333;
    border-color: #555;
}

.black-and-white .table-responsive table.table th.selected {
    background-color: rgba(228, 7, 0, .5);
}

.black-and-white .btn.active {
    color: #fff;
    background-color: #8B1D19;
}

.black-and-white .tanks-row .stats-table {
    background-color: rgba(0, 0, 0, .25);
}

.black-and-white .tanks-row .stats-table .stats-row.selected {
    background-color: rgba(0, 0, 0, .25);
    text-shadow: none;
}

.form-control {
    color: #DDD;
    background-color: #222;
}

.footer {
    background-color: #111;
    color: #DDD;
}

.black-and-white .playerstats-achievements .achievements-col {
    background-color: rgba(0, 0, 0, 0);
}

.left-b,
.right-b,
.support-banner {
    display: none;
}


.black-and-white .tank-filters .filter-nations .btn {
    border: 3px solid #333;
}

.clan-supporters-page-footers .recent-template,
.new-header .header-first-bar,
.recent,
.black-and-white .tank-filters .filter-nations .btn.active,
.black-and-white .tanks-extra .tanks-filters .tank-filters .btn-primary.active,
.blitz-stars,
.dropdown-menu a.selected,
.clan-stars .clan-stars-table .clan-stars-row.clan-stars-header .sort-col,
.clan-stars .clan-stars-table .clan-stars-row.clan-stars-header .csc:nth-of-type(n+3):hover {
    background-color: #8B1D19;
}

.black-and-white .tank-filters .filter-nations .btn.active {
    border-color: #8B1D19;
}

.black-and-white .playerstats-super .clan-info-dropdown a {
    color: #8B1D19;
}

.clan-stars .clan-stars-table .clan-stars-row.clan-stars-header .sort-col {
    border-bottom: 2px solid rgba(255, 255, 255, .75);
}

.clan-stars .clan-stars-table .clan-stars-row.clan-stars-header .csc:nth-of-type(n+3):hover {
    background-color: #444;
    border-bottom: 2px solid rgba(255, 255, 255, .75);
}

.magical-tank-view .main-window-content {
    color: #ddd;
    background-color: #222;
}

.black-and-white .magical-tank-view .main-window .title-bar {
    background-color: #111;
    color: #ddd;
}

.dropdown-menu > li > a {
    color: #ddd
}


.tanks-extra .tank-column-filters {
    background-color: #111;
    color: #ddd;
}

.tanks-extra .tank-column-filters h1,
.tanks-extra .tank-column-filters h2 {
    color: #72b4db;
}

.tankTable--JWOoW {
    background-color: #222 !important;
}

.black-and-white .playerstats-header .winrate-ribbon-popup {
    background-color: #333;
    color: #ddd;
}

.black-and-white .playerstats-super h2 a {
    color: #fff;
}



.player-name-history-wrapper {
    color: #ddd;
}
.wrap--35Yy7 {
    background-color: #333;
    border-radius: 5px
}

.main-svg {
    background-color: rgba(0, 0, 0, 0) !important;
}

.clan-page .discord-button {
    background-color: #333;
    box-shadow: 1px 2px 5px #111;
}

.history-selection ul a:hover {
    border-bottom: 2px solid rgba(255, 255, 255, .75);
    background-color: #444;
}

.clan-page .sort-dropdown .btn {
    color: #BBB;
    background-color: #333;
    border-color: #555;
}
}
