css:
@-moz-document domain(casinotopsonline.com) {
.module-anchor {
	position: sticky;
	top: 9.2rem;
	margin: 2.4rem 0 2.4rem;
	z-index: 2;
	filter: drop-shadow(0 .1rem .1rem rgba(0, 0, 0, .16));
}
.cluster {
	margin: 2.4rem 0 1.6rem;
	filter: drop-shadow(0 .1rem .1rem rgba(0, 0, 0, .16));
}
.module-anchor .anchor-header, .cluster .cluster-header {
	background-color: #3F3F3F;
	color: #fff;
	display: flex;
	align-items: center;
	justify-content: center;
	height: 4.2rem;
	padding: 0 1.6rem;
	font-weight: 500;
	font-size: 1.2rem;
	border-radius: 3px 3px 0 0;
	text-transform: uppercase;
	font-kerning: auto;
	text-rendering: auto;
}
.module-anchor .anchor-header p, .cluster .cluster-header .title {
	font-weight: 500;
	font-size: 1.3rem;
}
.module-anchor .anchor-body, .cluster .cluster-body {
	border-radius: 0 0 3px 3px;
}
.module-anchor .anchor-body ul .anchor-item, .cluster .cluster-body ul .cluster-item {
	background-color: #fff;
	border-bottom: 1px solid #EDEDED;
	transition: all .3s ease-in-out;
}
.cluster .cluster-body ul .cluster-item {
	min-height: unset;
	max-height: unset;
	padding: 0;
}
.module-anchor .anchor-body ul .anchor-item:last-child, .cluster .cluster-body ul .cluster-item:last-child {
	border-bottom: none;
}
.module-anchor .anchor-body ul .anchor-item:last-child {
	border-radius: 0 0 3px 3px;
}
.module-anchor .anchor-body ul .anchor-item a, .cluster .cluster-body ul .cluster-item a {
	min-height: 48px;
	display: flex;
	align-items: center;
	color: #4D4D4D;
	line-height: normal;
	margin: 0;
	font-size: 1.5rem;
	font-weight: 400;
	padding: 1.2rem 2.4rem;
	border-left: solid transparent;
	border-width: 2px;
	padding-left: 2.2rem;
	transition: border-color .3s ease-in-out;
	font-kerning: auto;
	text-rendering: auto;
}
/* Active */
 .cluster .cluster-body ul .cluster-item.active {
	color: #4D4D4D;
	border-left: solid #DB5B63;
	border-width: 2px;
	padding-left: 2.2rem;
	background-color: #fff;
	border-bottom: 1px solid #ededed;
}
.cluster .cluster-body ul .cluster-item:hover {
	background-color: #F0F0F0;
}
.cluster .cluster-body ul .cluster-item.active:hover {
	background-color: #fff;
}
.cluster .cluster-body ul .active {
	background: #ffffff;
	color: #4D4D4D;
	cursor: default;
}
.cluster .cluster-body ul .cluster-item .active {
	display: inherit;
	align-items: inherit;
	width: 100%;
	line-height: normal;
	margin: 0;
	height: auto;
	min-height: 48px;
	font-kerning: auto;
	text-rendering: auto;
}
.module-anchor .anchor-body ul .anchor-item.active a {
	color: #4D4D4D;
	border-left: solid #DB5B63;
	border-width: 2px;
	padding-left: 2.2rem;
	background-color: #fff;
	font-size: 1.5rem;
	font-weight: 400;
}
.module-anchor .anchor-body ul .anchor-item:hover {
	background-color: #F0F0F0;
}
.module-anchor .anchor-body ul .anchor-item.active {
	background-color: #fff;
}
/* MORE */
 .cluster .cluster-body .js-view-more {
	border-radius: 0 0 3px 3px;
	font-size:1.3rem;
	font-weight:600;
	color: #4D4D4D;
	background-color: #F7F7F7;
	text-transform: uppercase;
	transition: all .3s ease-in-out;
	max-height: unset;
	padding: 0;
	height: 3.6rem;
}
.cluster .cluster-body ul .cluster-item:last-child {
	border-bottom: none;
	border-radius: 0 0 3px 3px;
}
.cluster .cluster-body .js-view-more:focus, .cluster .cluster-body .js-view-more:hover {
	color: #9A373D;
	background-color: #FCF3F4;
}
span.arrow-icon {
	transition: transform .3s ease-in-out;
	opacity: 0.5;
	display: none;
}
.cluster .cluster-body ul .cluster-item:hover span.arrow-icon {
	transform: translate(.4em, 0);
	opacity: 1.0;
}}
