css:
@-moz-document domain("astutesolutions.atlassian.net") {
#ghx-work-wrapper {
    margin-right: 0px;
}

#ghx-pool-column {
    padding-right: 0px;
    height: 100%;
}
/* weird scroll when it fits the page */
.ghx-swimlane:last-of-type {
	margin-bottom: 0;
}
/* end weird */
#ghx-work{
    display: block;
    table-layout: unset;
    overflow: scroll !important;
}
.ghx-columns .ghx-column {
    width: 380px;
    padding: 0;
}

.ghx-parent-group,
.ghx-subtask-group .ghx-issue,
.ghx-parent-group .ghx-issue {
    background: #ffefdd;
}

.ghx-wrap-issue {
    width: 380px;
    display: flex;
    flex-wrap: wrap;
}

.ghx-parent-group {
    width: 183px;
    margin: 3px;
    /*background: #adbfd3;*/
}

.ghx-issue+.ghx-subtask-group, .ghx-parent-stub+.ghx-subtask-group {
    padding: 0
}

.ghx-subtask-group .ghx-issue {
    margin: 0;
    padding: 0 0 0 13px;
}
.ghx-issue {
    width: 168px;
    margin: 3px;
    padding: 3px;
    min-height: 90px;
}

/* resize left panel */
.css-hgbud4,
.css-quddf1 {
	width: 5px;
}
/* end resize left panel */
}
