@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gamer.ru") {

/* ADV Block */
.col_left > div > .left_side_block:nth-child(2),
.col_left > div > .left_side_block:nth-child(3),
.col_left > div > .left_side_block:last-child,
[id^="begun_block"],
.main_box.columns > .col_right_top table[width="630"]
{ display:none !important; }


/* Fix for margins */
.main_box .col_left .also_in_this_blog_sidebar { margin-bottom:15px !important; }
[class^=profile] .sub_menu { margin-top:-10px !important; }
}
