css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(linuxquestions.org){

   html, body {
      background-color: #000000 ! important; 
      background-image: url('data:image/png;base64,') ! important; 
      background-repeat: repeat-x ! important; 
      color: #ffffff ! important;
   }

INPUT, TEXTAREA, .bginput{
   opacity:.8 !important;
}
PRE{
max-width: 700px !important;
}
.bginput:hover, TEXTAREA:hover, INPUT:hover{
opacity: .9 !important
}
.bginput:focus, TEXTAREA:focus, INPUT:focus{
opacity: 1 !important
}
a:link, body_alink{
color:#00ff00 !important;text-decoration:none !important;
}
a:visited, body_avisited{
color: #aaaa88 !important;text-decoration:none !important;
}
a:hover, a:active, body_ahover{
color: #00ff00 !important;text-decoration:none !important;
}
.page{
background:#000000 !important;
color: #ffffff !important
}
td, th, p, li{
border-color:#000 !important;
}
.tborder{
background:#000 !important;border:1px solid #000 !important;color:#000000 !important;
}
.tcat{
background:#141026 !important;color:#ffffff !important;
}
.tcat a:link, .tcat_alink{
color:#ffffff !important;text-decoration:none !important;
}
.tcat a:visited, .tcat_avisited{
color:#ffffff !important;text-decoration:none !important;
}
.tcat a:hover, .tcat a:active, .tcat_ahover{
color:#ffffffF66 !important;text-decoration:underline !important;
}
.thead{
background:#46357C !important;color:#ffffffFFF !important;
}
.thead a:link, .thead_alink{
color:#ffffffFFF !important;
}
.thead a:visited, .thead_avisited{
color:#ffffffFFF !important;
}
.thead a:hover, .thead a:active, .thead_ahover{
color:#ffffffF00 !important;
}
.tfoot{
background:#141026 !important;color:#000000 !important;
}
.tfoot a:link, .tfoot_alink{
color:#ffffffFFF !important;
}
.tfoot a:visited, .tfoot_avisited{
color:#ffffffFFF !important;
}
.tfoot a:hover, .tfoot a:active, .tfoot_ahover{
color:#ffffffF66 !important;
}
.alt1, .alt1Active{
background:#6F5C9A !important;color:#ffffff !important;
}
.alt2, .alt2Active{
background: #45357C !important;color:#ffffff !important;
}
.inlinemod{
background:#ffffffFCC !important;color:#000000 !important;
}
.wysiwyg{
background:#EEEEEE !important;color:#000000 !important;
}
FONT[color$=""], SPAN[style*="color: rgb(0, 0, 187)"]{
color:#bbccff !important;
}
.smallfont{
color:#00ff00 !important;
}
.time{
color:#FFCC66 !important;
}
LABEL{
color:#f1f1f1 !important;
}
.highlight{
color:#FF0000 !important;font-weight:bold !important;
}
.fjsel{
background:#EEEEEE !important;color:#000000 !important;
}
.fjdpth0{
background:#777 !important;color:#f1f1f1 !important;
}
.fjdpth1{
background: #000000 !important;
}
SELECT, OPTION{
background:#555 !important;border-color:#555 !important;color:#f1f1f1 !important;-moz-appearance:none !important;
}
.panel, .fieldset, .vBulletin_editor{
   background:#6F5C9A !important;
   border-color:#000 !important;
   color:#e5e5e5 !important;
}
.panelsurround{
background:#46357C !important;color:#ffffff !important;
}
legend{
color:#FFCC66 !important;
}
.vbmenu_control{
background:#141026 !important;color:#ffffffFFF !important;
}
.vbmenu_control a:visited, .vbmenu_control_avisited{
color:#D2D454 !important;
}
.vbmenu_control a:hover, .vbmenu_control a:active, .vbmenu_control_ahover{
text-decoration:underline !important;
}
.vbmenu_option{
background:#4A4A4A !important;color:#000000 !important;
}
.vbmenu_hilite{
background:#46357C !important;
}
.shade, a.shade:link, a.shade:visited, #main_userinfo h1, #main_userinfo h2{
color:#f1f1f1 !important;
}
.bbcodeblock {
   background: #333333 !important;
   color: #aaaa88;
   background-image: none ! important;
   border: 4px ! important;
   border-color: black;
}
img[src$="http://e1h13.simplecdn.net/lqcdn/images/questions/images/LinuxQuestions.png"] {
   visibility: hidden;
   height: 0px;
}
}
