css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("your.build.server") {
.main-panel, .Footer {
   display:none;
}

.wholepage{
   background-color:#DCE4EA !important;
}

#StatusGrid td:nth-child(5),
#StatusGrid td:nth-child(6),
#StatusGrid td:nth-child(1),
#RefreshForm{
   display:none;
}

#StatusGrid td:nth-child(8){
   width:50px !important;
   height:30px !important;
}

#StatusGrid td:nth-child(8) font[color="black"]{
   overflow:hidden;
   height:18px !important;
   display:block;
}

#StatusGrid td:nth-child(8) font[color="black"]:hover{
   overflow:visible;
}

.TopControls{
   background-color:#778899 !important;
}

.TopControls p{
   display:none;
}

.breadcrumbs a{
   color:#000000 !important;
   text-shadow: 1px 1px 5px #2F4F4F;
   -moz-border-radius: 4px; 
   border: 1px solid #000; 
   padding: 2px 10px;
   background-color:#B0C4DE;
}

.breadcrumbs a:hover{
   background-color:#9DB0C2;
}

#LeftHandSide{
   background-color:#DCE4EA !important;
}

#Plugins img,
#LeftHandSide{
   width:140px !important;
}

#LeftHandSide a{
   text-decoration:none !important;
   text-shadow: 1px 1px 1px darkgrey;
}

.sectionheader{
   background-color:#CCCCCC !important;
   color:black !important;
   text-shadow: 1px 1px 1px darkgrey;
   -moz-border-radius: 4px; 
   border: 1px solid #000; 
   padding: 10px;
}

.RecentBuildsPanel a{
   text-shadow:none !important;
   font-size:10px;
   font-family:verdana;
   padding-bottom:10px;
   display:block;
}

.RecentBuildsPanel th{
   background-color:white !important;
   color:black !important;
}

.RecentBuildsPanel{
   border:none !important;
}

.ProjectGridHeader a,
.ProjectGridHeader b{
   color:black !important;
   text-shadow: 1px 1px 5px darkgrey;
}

#StatusGrid{
   border-color:#DEDEDE !important;
   padding:0 !important;
}

#StatusGrid a{
   color:black !important;
   text-decoration:none !important;
   display:block;
}

#StatusGrid tr:nth-child(odd) {
   background-color:white !important;
}

#StatusGrid tr:nth-child(even) {
   background-color:#F0F8FF !important;
}

img{
   display:none !important;
}

input[name="StopBuild"],
input[name="StartBuild"],
a[href="/CCTrayDownload.aspx"] {
   display:none;
} 

table[bgcolor="#333399"]{
   background-color:transparent !important;
}
}
