css:
/*Originally made by Blocks_404, modified by firedbloxy*/

@-moz-document domain(brick-hill.com)
{
/*Originally made by Blocks_404, modified by firedbloxy*/

  .slider-button
  {
    top: 175px;
    position: absolute;
    text-align: center;
    color: #9D84B7;
    z-index: 1000;
    padding: 5px;
    transition: background-color .1s linear;
  }

  .thread-label
  {
    font-size: .6em;
    color: #9D84B7;
    margin-right: 4px;
    padding: 4px;
    border-radius: 4px;
  }

  button:not(.plain):not([disabled]).bits,
.button:not(.plain):not([disabled]).bits
  {
    color: #9D84B7;
    background-color: #311646;
    border-color: #9D84B7;
  }

  .sellers .seller:not(:last-child)
  {
    padding-bottom: 5px;
    margin-bottom: 10px;
    border-bottom: 1px solid #9D84B7;
  }

  .sellers .price .serial
  {
    font-size: .8em;
    color: #9D84B7;
    text-align: right;
  }

  button:not(.plain):not([disabled]).bucks,
.button:not(.plain):not([disabled]).bucks
  {
    color: #9D84B7;
    background-color: #311646000;
    border-color: #9D84B7;
  }

  .card .footer
  {
    border-top: 1px solid #9D84B7;
    padding: 10px;
    background-color: #311646;
  }

  .bucks-text
  {
    color: #9D84B7;
  }

  nav div ul li a
  {
    display: inline-block;
    padding: 8px 5px;
    border-bottom: 2px solid #9D84B7;
  }

  .profile-card.award img
  {
    height: 128px;
    border: 1px solid #9D84B7;
    border-radius: 5px;
  }

  .crate-types
  {
    border-right: 2px solid #9D84B7;
    padding-right: 5px;
  }

  .status-dot
  {
    float: none;
    display: inline-block;
    margin-bottom: 3px;
    background-color: #9D84B7;
    border-radius: 50%;
    width: 10px;
    height: 10px;
  }

  .card .footer .playing
  {
    text-align: right;
    color: #9D84B7;
    font-weight: 600;
  }

  element.style
  {
    color: #9D84B7;
  }

  body
  {
    color: #9D84B7;
    background-color: #311646;
    font-family: consolas;
  }

  nav div.primary
  {
    background-color: #311646;
    color: #9D84B7;
  }

  nav div.secondary
  {
    background-color: #311646;
    color: #9D84B7;
  }

  nav .info
  {
    background-color: #311646;
  }

/*cards*/
  input,
textarea
  {
    padding: 4px;
    color: #9D84B7;
    border-radius: 5px;
    background-color: #9D84B7;
    border: 1px solid #9D84B7;
  }

  .item-card-container
  {
    text-align: left;
    background-color: #311646;
    border-radius: 3px;
    box-shadow: 0px 2px 5px #9D84B7;
    padding: 5px;
    width: 100%;
    box-sizing: border-box;
  }

  .select,
select
  {
    background-color: #311646;
    border-color: #9D84B7;
    border-radius: 3px;
    font-weight: 600;
    padding: 8px;
    color: #9D84B7;
  }

  button:not(.plain):not([disabled]).orange,
.button:not(.plain):not([disabled]).orange
  {
    color: #9D84B7;
    background-color: #311646;
    border-color: #9D84B7;
  }

  button:not(.plain):not([disabled]).green,
.button:not(.plain):not([disabled]).green
  {
    color: #9D84B7;
    background-color: #311646;
    border-color: #9D84B7;
  }

  .avatar-card.crate .img-holder
  {
    width: 100%;
    border: 1px solid #9D84B7;
    border-radius: 5px;
    padding: 10px;
  }

  input.rigid,
textarea.rigid
  {
    border-radius: 2px;
    color: #9D84B7;
    padding: 10px;
    background-color: #311646;
    border: 2.5px solid #9D84B7;
  }

  button:not(.plain):not([disabled]).blue,
.button:not(.plain):not([disabled]).blue
  {
    color: #9D84B7;
    background-color: #311646;
    border-color: #9D84B7;
  }

  .dropdown-content li *
  {
    background-color: #311646;
    border-radius: 5px;
    padding: 10px 0 10px 5px;
    display: block;
    transition: background-color 0.1s;
  }

  .dropdown-arrow
  {
    font-size: 20px;
    color: #9D84B7;
    cursor: pointer;
  }

  footer a
  {
    color: #9D84B7;
  }

  .gray-text
  {
    color: #9D84B7;
  }

  .dark-gray-text
  {
    color: #9D84B7;
  }

  footer
  {
    position: absolute;
    bottom: 0;
    width: 100%;
    background-color: #311646;
    text-align: center;
    color: #9D84B7;
    padding: 20px 0;
  }

  .blue-text
  {
    color: #9D84B7;
  }

  input,
textarea
  {
    padding: 4px;
    color: #9D84B7;
    border-radius: 5px;
    background-color: #311646;
    border: 1px solid #9D84B7;
  }

  .button:not(.plain):not([disabled]).blue
  {
    color: #9D84B7;
    background-color: #311646;
    border-color: #9D84B7;
  }

  .agray-text
  {
    color: #9D84B7;
  }

  .red-text
  {
    color: #9D84B7;
  }

  .favorite-text
  {
    color: #9D84B7;
  }

  .card .content
  {
    background-color: #311646;
  }

  .line,
hr
  {
    border-top: 1px solid #311646;
    border-bottom: 1px solid #9D84B7;
  }

  .toggle-user-desc:not(.open):after
  {
    background-color: #311646;
  }

  .read-more-desc
  {
    background-color: #311646;
  }

  .avatar-card.crate img
  {
    background-color: #fdfdfd00;
  }

  .forum-bar .fa-angle-double-right
  {
    color: #9D84B7;
  }

  .set-list .set-stats li div:first-child
  {
    color: #9D84B7;
  }

  .blockquote.blue,
blockquote.blue
  {
    border-color: #9D84B7;
  }

  .blockquote.green,
blockquote.green
  {
    border-color: #9D84B7;
  }

  .blockquote.blue,
blockquote.red
  {
    border-color: #9D84B7;
  }

  .blockquote.green,
blockquote.orange
  {
    border-color: #9D84B7;
  }

  .pages .page.active,
.pages.green .page.active,
.pages.red .page.active,
.pages.orange .page.active
  {
    color: #9D84B7;
  }

  .pages .page
  {
    color: #9D84B7;
  }

  .hover-card.viewed
  {
    background-color: #311646;
  }

  .hover-card:hover,
.profile-card:hover,
.friend-card:hover,
.crate-types :hover
  {
    background-color: #311646;
  }

  button:not(.plain),
.button:not(.plain)
  {
    text-align: center;
    padding: 10px 20px;
    border-radius: 5px;
    border: 1px solid;
    display: inline-block;
    text-transform: uppercase;
    color: #9D84B7;
    background-color: #311646;
    border-color: #311646;
  }

  .crate-types .active,
.crate-types :hover
  {
    background-color: #311646;
    border-left: 4px solid #311646;
  }

  .trade-picker
  {
    margin-top: 10px;
    overflow: hidden;
    overflow-y: scroll;
    border-radius: 5px;
    background-color: #311646;
    max-height: 1024px;
  }

  hr,
.line
  {
    border: 0;
    height: 0;
    border-top: 1px solid #9D84B7;
    border-bottom: 1px solid #311646;
  }

  .trade-picker .trade.selected
  {
    background-color: #311646;
    border-left: 4px solid #9D84B7;
  }

/*cards top*/
  .card .top:not(.absolute)
  {
    color: #9D84B7;
    background-color: #311646;
    border-bottom: 2px solid;
    border-color: #9D84B7;
  }

  .card .top:not(.absolute).blue
  {
    color: #9D84B7;
    background-color: #311646;
    border-color: #9D84B7;
  }

  .card .top:not(.absolute).green
  {
    color: #9D84B7;
    background-color: #311646;
    border-color: #9D84B7;
  }

  .card .top:not(.absolute).orange
  {
    color: #9D84B7;
    background-color: #311646;
    border-color: #9D84B7;
  }

  .card .top:not(.absolute).red
  {
    color: #9D84B7;
    background-color: #311646;
    border-color: #9D84B7;
  }

  .thread-label.blue
  {
    background-color: #9D84B7;
  }

  .forum-options
  {
    color: #9D84B7;
  }

  .thread-label.green
  {
    background-color: #311646000;
  }

  .thread-label.orange
  {
    background-color: #9D84B7;
  }

  .thread-label.red
  {
    background-color: #9D84B7;
  }

/*buttons*/
  button:not(.plain).blue,
.button:not(.plain).blue
  {
    color: #9D84B7;
    background-color: #311646;
    border-color: #9D84B7;
  }

  button:not(.plain):not([disabled]).red,
.button:not(.plain):not([disabled]).red
  {
    color: #9D84B7;
    background-color: #311646;
    border-color: #9D84B7;
  }

  button:not(.plain).green,
.button:not(.plain).green
  {
    color: #fff;
    background-color: #311646;
    border-color: #9D84B7;
  }

  button:not(.plain).orange,
.button:not(.plain).orange
  {
    color: #fff;
    background-color: #009f28;
    border-color: #007D1F;
  }

  .button.tab-button.transparent,
button.tab-button.transparent
  {
    background-color: transparent;
    border-color: transparent;
    color: #fff;
  }

  button.tab-button.transparent,
.button.tab-button.transparent
  {
    background-color: rgba(0, 0, 0, 0);
    border-color: rgba(0, 0, 0, 0);
    color: #9D84B7;
  }

  .input-group .input-button
  {
    border-left: 1px solid #9D84B7;
    color: #9D84B7;
    background-color: #311646;
  }

/*tabs*/
  .tabs .tab-body
  {
    border: 1px solid #9D84B7;
    background-color: #311646;
    border-top: 0px;
  }

  .profile-card.crate img
  {
    width: 100%;
    border: 1px solid #9D84B7;
    background-color: #311646;
    border-radius: 5px;
    padding: 5px;
  }

  .box.shaded
  {
    background-color: #311646;
  }

  .black-text
  {
    color: #9D84B7;
  }

  .forum-bar a
  {
    color: #9D84B7;
  }

  .admin-forum-options a,
.admin-forum-options button
  {
    color: #9D84B7;
    display: inline-block;
    text-decoration: none;
    font-weight: 500;
    font-size: 16px;
    background-color: transparent;
    border: none;
    padding: 0 20px 0 0;
  }

  .tabs .tab.active
  {
    background-color: #311646;
    border-bottom: 1px solid #9D84B7;
  }

  .tabs .tab
  {
    text-align: center;
    color: #9D84B7;
    border-radius: 5px 5px 0px 0px;
    padding: 10px;
    background-color: #311646;
    border: 1px solid;
    border-color: #9D84B7;
    font-size: 1.1rem;
  }

  .pages.blue .page.active
  {
    color: #9D84B7;
  }

/*alerts*/
  .alert.warning
  {
    background-color: #ef9526;
    border: 1px solid #d0780c;
  }

  .input-group
  {
    display: inline-flex;
    margin-bottom: 4px;
    overflow: auto;
    border-radius: 5px;
    border: 1px solid #9D84B7;
  }

  .light-gray-text
  {
    color: #9D84B7;
  }

  .label.dark
  {
    color: #9D84B7;
  }

  .label
  {
    font-size: 0.9em;
    font-weight: 500;
    color: #9D84B7;
  }

  .stat .title
  {
    font-weight: 500;
    font-size: 0.9em;
    color: #9D84B7;
  }

  .forum-links a
  {
    color: #9D84B7;
    padding: 0 10px;
  }

  .forum-tag:after
  {
    right: 100%;
    top: 50%;
    border: solid transparent;
    content: " ";
    height: 0;
    width: 0;
    position: absolute;
    border-right-color: #9D84B7;
    border-width: 5px;
    margin-top: -5px;
  }

  .dropdown-content.logout-dropdown li *
  {
    color: #9D84B7;
    background-color: #311646;
    border: solid #9D84B7;
  }

  .bits-text
  {
    color: #9D84B7;
  }

  .forum-tag
  {
    position: relative;
    background: #311646;
    border-radius: 4px;
    padding: 6px 8px;
    color: #9D84B7;
    float: right;
  }

  .alert.error
  {
    background-color: #311646;
    border: 1px solid #9D84B7;
  }

  .alert.success
  {
    background-color: #311646;
    border: 2px solid #9D84B7;
  }

  .w-full
  {
    width: 100%;
    color: #fdfdfd00;
  }

  .bg-gradient-gray
  {
    background-image: -webkit-linear-gradient( top,
    #71baed00,
    #c51105 0%,
    #016b1e 100%,
    #71baed00);
  }

  .text-gray-600
  {
    color: #311646;
  }

  .alert
  {
    margin-bottom: 10px;
    border-radius: 5px;
    text-align: center;
    padding: 10px;
    color: #9D84B7;
  }

  .darkest-gray-text
  {
    color: #9D84B7;
  }

  .ablack-text
  {
    color: #9D84B7;
  }

  .card .item-creator
  {
    overflow: hidden;
    color: #9D84B7;
    font-size: 0.9em;
  }

  .shop-categories .category
  {
    flex: 1;
    color: #9D84B7;
    text-align: center;
  }

  .select-color-text
  {
    color: #9D84B7;
  }

  .card .creator
  {
    overflow: hidden;
    color: #9D84B7;
    font-size: 0.9em;
  }

  .card .name
  {
    overflow: hidden;
    color: #9D84B7;
    font-weight: 600;
    display: inline-block;
  }

  .shop-categories .category a:hover,
.shop-categories .category.active
  {
    font-weight: 600;
    color: #9D84B7;
    cursor: pointer;
  }

  .card .item-card-details,
.card .item-content
  {
    font-weight: 500;
    background-color: #311646;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    border-top: 1px solid #9D84B7;
    padding: 10px;
    max-height: 81px;
  }

  .white-text
  {
    color: #9D84B7;
  }

  .membership-buttons .membership-button.royal
  {
    background: linear-gradient(0deg, #311646 0%, #311646 50%);
  }

  .membership-buttons .membership-button .membership-average.royal
  {
    background-color: #311646;
  }

  .lottery-button.plain
  {
    padding: 15px 50px;
    margin-top: 10px;
    background: linear-gradient(0deg, #311646 0%, #311646 100%);
  }

  .membership-holder .membership-header.royal
  {
    background-color: #311646;
  }

  .membership-buttons .membership-button .membership-average.ace
  {
    background-color: #311646;
  }

  .membership-buttons .membership-button.ace
  {
    background: linear-gradient(0deg, #311646 0%, #311646 50%);
  }

  .membership-buttons
  {
    padding: 10px;
    border: 1px solid #311646;
    border-top: 0;
    text-align: center;
  }

  .membership-buttons .membership-button.bucks
  {
    background: linear-gradient(0deg, #311646 0%, #311646 100%);
  }

  .membership-holder .membership-header.ace
  {
    background-color: #311646;
  }

  .modal .modal-content
  {
    background-color: #311646;
    margin: 15% auto;
    padding: 20px;
    width: 20%;
    min-width: 250px;
  }

  .box
  {
    border: 1px solid #9D84B7;
    border-radius: 5px;
  }

  .file-img-box
  {
    border: 1px solid #9D84B7;
    width: 156px;
    height: 156px;
    margin: auto;
    margin-bottom: 10px;
  }

  .upload-preview-box
  {
    position: relative;
    border: 1px solid #9D84B7;
    border-radius: 5px;
    text-align: center;
    padding: 5px;
    height: 256px;
    width: 256px;
    margin: auto;
  }

  .modal .cancel-button
  {
    background-color: #311646;
    color: #9D84B7;
    border: 1px solid #9D84B7;
  }

  button:not(.plain):not([disabled]).free,
.button:not(.plain):not([disabled]).free
  {
    color: #9D84B7;
    background-color: #311646;
    border-color: #9D84B7;
  }

  .offsale-text
  {
    color: #9D84B7;
  }

  .card .thumbnail.dark
  {
    background-color: #311646;
  }
}
