css:
@-moz-document url-prefix('https://www.skyroom.online'){
body{  
	color:#00e7ff;
	/*background-color:#142850; */
    background:url('https://8pic.ir/uploads/1892392.jpg') fixed no-repeat center center;
	background-size:cover;    
	width:100%;
    height:100%;
}
#page_room > header{
	/*background-color:#05357d;
    border-bottom:1px solid #ccc;
    box-shadow:0 5px 5px #0C7B93;*/
  	display:none !important;
}
.block{
    border-radius:10px;
    border:none;
	background-color:#05357d;
    /*box-shadow:0 0 15px #0C7B93;*/
  	box-shadow: rgba(4, 81, 147, 0.3) 0px 19px 38px, rgba(4, 81, 147, 0.22) 0px 15px 12px;
}
.block-content{
	background-color:#142850;
    border:none;
	border-radius:10px;
}
.message-box-content{
    background-color:#142850;
    box-shadow: 0 0 5px #ccc;
	border-radius:10px;
    width:100%;
    height:100%;
}
.input.message-box-wrapper{
	background-color:#142850;
   	border-radius:10px;
    border:none;
}
.input.is-rtl{
	background-color:#05357d;
}
.commands-box{
	background-color:#0C7B93 !important;
	border-radius:10px;
    overflow:hidden;
}
div.col.box-grow{
	padding:1px;
    overflow:hidden;
}
.chat-msg{
	background-color:#045193 !important;
    border-right-style: solid;
  	border-radius: 10px !important;
	border-bottom-right-radius: 0 !important;
}
.quoted-message{
 	background-color: #0C7B93 !important ;
	border-right: 3px solid #00e7ff !important ;
  	border-radius:5px !important;
  	border-bottom-right-radius : 0 !important;
  }
.user-row:hover{
	background-color: #0c7b93 !important;
    transition: all .3s ease;
    border-radius: 5px;
    border-bottom-left-radius: 0;
    border-bottom-right-radius: 0;
  	padding:3px 5px;
    border-bottom: 1px solid #fff;
}
.ps__thumb-y {
    background-color: #00e7ff;
    border-radius: 5px;
}
.ps__thumb-y:hover{
	    background-color: #00e7ff !important;
}
.dropdown .dropdown-toggle{
	background:#045193 !important;
	border-radius: 5px;
}
.dropdown .dropdown-toggle:hover {
    background: #05357d;
    border-color: transparent;
    transition: all .3s ease-in-out;
    transform: rotate(360deg);
  	cursor:pointer;
  	box-shadow: 0 0 5px #142850;
}
.room-nav{
	margin-top: 5px;
  	margin-bottom:10px;
}
div#toolbar{
	background: #142850;
    border-radius: 5px;
    padding: 0 5px;
  	box-shadow: rgba(50, 50, 93, 0.25) 0px 6px 12px -2px, rgba(0, 0, 0, 0.3) 0px 3px 7px -3px;
}
div#toolbar button{
	cursor:pointer;
}
#sidebar > div.col.box.block.users:hover{
  	transition: all .3s ease-out;
	box-shadow: 8px 5px 15px #142850;
}
#sidebar > div.col.box.block.chat.acl-unpin-not-allowed:hover{
	transition: all .3s ease-out;
	box-shadow: 8px 5px 15px #142850;
}
.reply-to-button{
	background : #045193 !important;
  	border:none
}
  .chat .chat-msg .message-commands
  {
    background-color: transparent;
  }
div.col.dialog.user-info-dialog.draggable,.dialog.draggable,.settings{
  	border: none;
    border-radius: 5px;
    background: #05357d;
  	transition:all .3s ease-in-out;
  }
  div.col.dialog.user-info-dialog.draggable:hover,.dialog.draggable:hover,.settings:hover{
  box-shadow: 0 0 15px 1px #142850;
  }
  nav.menu.popup.visible ul {
    background: #142850 !important;
    border: none;
    box-shadow: 0 0 5px #00e7ff;
    border-radius: 10px;
    overflow:hidden
}
  nav.menu.popup.visible ul li:hover{
  	  	transition:all .3s ease-in-out;
		background-color: #0c7b93;
  }
}
