css:
@-moz-document regexp("^https://(?:testnet\\.|devnet\\.)?maiar\\.exchange.*") {
/*
  * Created by janniksam -> under development
  * Based on RED style by RO_engineer
  */
 div.layout {
    background-image: linear-gradient(to right, DeepPink, HotPink, Pink) !important;
}
/* Nav menu - main part */
 li.nav-item {
    padding-left: 1rem;
    padding-right: 1rem;
}
li.nav-item .nav-link {
    font-size: 1rem !important;
}
li.nav-item .nav-link.active {
    font-size: 1rem !important;
    background-color: MediumVioletRed;
    color: White;
}
li.nav-item .nav-link:hover, li.nav-item .nav-link:focus {
    background-color: RebeccaPurple !important;
    color: white !important;
}
/* Nav menu - right side */
 .home .btn.btn-light, .btn.btn-light {
    border-width: 0px;
    color: white !important;
}
.btn.btn-light {
    border-width: 0px;
}
.home .btn.btn-light:hover, .home .btn.btn-light:focus, .home .btn.btn-light:active, .btn.btn-light:hover, .btn.btn-light:focus, .btn.btn-light:active {
    background-color: RebeccaPurple !important;
    color: white !important;
}
/* logo on the right side */
 .mex-modal-btn:hover, .mex-modal-btn:focus {
    background-color: RebeccaPurple !important;
}
nav button div.text-left {
    font-size: 1rem !important;
    font-weight: bold;
}
nav.navbar .token-symbol {
    /* Not needed if fill: #d22; is applied */
    /*filter: hue-rotate(110deg);*/
}
/* light on/off & 3 dots */
 div#links-popover .popover-body .card .card-body a {
    font-size: 1rem !important;
}
div#links-popover .popover-body .card .card-body a:hover {
    font-size: 1rem !important;
    background-color: RebeccaPurple !important;
    color: white !important;
}
div#links-popover .popover-body .card .card-body a:active {
    font-size: 1rem !important;
    background-color: RebeccaPurple !important;
    color: white !important;
}
.basic-popover#links-popover .dropdown-item.active {
    color: MediumVioletRed;
}
/* buttons */
 .main-content .btn:not(.farm-action-btn) {
    font-size: 1rem !important;
    border-width: 0px;
}
.main-content .btn:hover, .main-content .btn:focus {
    background-color: RebeccaPurple !important;
    color: white !important;
}
.home .text-secondary {
    color: White !important;
}
.text-secondary {
    color: White !important;
}
.btn.btn-primary {
    background-color: MediumVioletRed !important;
}
.btn-primary:hover, .btn-primary:focus {
    background-color: RebeccaPurple !important;
}
.main-content .btn.btn-outline-primary-glow {
    background-color: MediumVioletRed;
    box-shadow: inset 0px 0px 10px 0px rgba(102, 51, 153, 0.5), 0px 0px 10px 0px rgba(199, 21, 131, 0.5) !important
}
/* drop downs */
 .styled-select__option--is-selected {
    background-color: MediumVioletRed !important;
}
.styled-select__control {
    border-color: #343439 !important;
    background-color: #18191a !important;
}
.styled-select__control--is-focused {
    border-color: MediumVioletRed !important;
    box-shadow: none;
}
.styled-select__control:hover {
    border-color: #9ba5b4 !important;
}
.styled-select__control.styled-select__control--is-focused {
    border-color: MediumVioletRed !important;
    box-shadow: none;
}
/* Info cards */
 div.info-card h3 {
    color: white !important;
    text-shadow: 2px 2px black !important;
}
div.info-card .text-secondary {
    color: rgb(155, 165, 180) !important;
    text-shadow: 1px 1px black !important;
}
/* Feature cards*/
 div.feature-card h3 {
    text-shadow: 2px 2px black !important;
}
div.feature-card small {
    color: rgb(155, 165, 180) !important;
    text-shadow: 1px 1px black !important;
}
div.feature-card .feature-icon-container {
    background-color: transparent !important;
}
div.feature-card .feature-icon {
    border-color: Pink !important;
    border-width: 3px !important;
    box-shadow: DarkMagenta 0px 0px 10px 0px inset, Pink 0px 0px 10px 0px !important;
}
/* Farm cards */
 .farm-card .rewards-badge {
    background-color: DarkMagenta;
}
.btn-dark:hover, btn-dark:focus, .btn.btn-dark:hover, .btn.btn-dark:focus, .btn.btn-secondary:hover, .btn.btn-secondary:focus {
    background-color: DarkMagenta !important;
}
/*Harvest Modal*/

/* Next pages */
 .card-arrow .text-primary-highlight {
    color: MediumVioletRed !important;
}
.main-content .container .row h2 {
    color: white !important;
}
/* MEX stats */
 .modal-content .card-footer .link-style:hover .modal-content .card-footer .link-style:focus {
    color: DarkMagenta !important;
}
/* Account details */
 .btn-close-link:hover, .btn-close-link:focus {
    color: DarkMagenta !important;
}
.card-body a.link-style:hover, .card-body a.link-style:focus {
    color: DarkMagenta !important;
}
/* Connect to a wallet popup */
 .modal-card .modal-card-body a:hover, .modal-card .modal-card-body a:focus {
    color: DarkMagenta !important;
}
.modal-card .modal-card-body .unlock-page .btn-unlock:hover, .modal-card .modal-card-body .unlock-page .btn-unlock:focus {
    border-color: RebeccaPurple !important;
    background-color: RebeccaPurple !important;
    color: white !important;
}
/* Other SVG logos */
 div.unlock-page svg.elrond-symbol path {
    fill: DarkMagenta!important;
}
div.token-symbol svg path {
    fill: DarkMagenta !important;
}
div.token-symbol svg path[fill="#2775ca"]:first-child {
	fill: white !important;
}
div.token-symbol svg path[fill="white"] {
	fill: white !important;
}
div.token-symbol {
    background-color: white !important;
}
.fa-spin {
    color: DarkMagenta !important;
}
.navbar-address {
    max-width: 8rem !important;   
}
}
