css:
@-moz-document domain("openvk.su") {
.page_header {
    background: url(https://i.imgur.com/on2hdEY.png) no-repeat;
    height: 42px;
}

.header_navigation {
    margin-top: -16px;
}


.header_navigation .link {
    background: none;
}

.header_navigation .link a {
    color: #DAE1E8;
    text-decoration: none;
    font-weight: bold;
}

input[type~="search"] {
    border: 1px solid #5579a0;
    padding: 3px;
    font-size: 11px;
    font-family: tahoma, verdana, arial, sans-serif;
    border-radius: 3px;
    height: 24px;
    width: 169px;
    position: absolute;
    margin-left: -630px;
    margin-top: 13px;
    background: url(https://i.imgur.com/N4qUhvd.png) no-repeat /* qualified */ !important;
    background-color: #fff;
    padding-left: 23px;
    
}

input[type~="search"][name~="query"] {
    width: 169px /* qualified */ !important;
/*     background: url(https://i.imgur.com/cz1tmu9.png) no-repeat 3px 4px qualified !important; */
    background-color: #fff /* qualified */ !important;
    padding-left: 22px /* qualified */ !important;
    padding-bottom: 5px /* qualified */ !important;
}

.content_title_expanded {
    border-top: #d3dae0 solid 1px;
    background-color: #e1e7ed;
    border-bottom: #dae1e8 1px solid;
    color: #45688E;
}

.content_subtitle {
    background-color: #F1F1F1;
    border-bottom: 1px solid #EEEEEE;
    color: #777777;
    border-top: none;
}


.navigation .link {
color:#2B587A;
}
.navigation .link:hover {
    background: 
#DAE1E8;
border-top: 1px solid
    #CAD1D9;
}
.tabs {
    border-bottom: 1px solid 
    #36638E;
}
#activetabs {
    background: 
    #36638E;
}
.button {
    border-radius: 2px;
    border: #2B587A;
    font-size: 11px;
    outline: none;
    white-space: nowrap;
    background: #2B587A;
    background-position: 0px -16px;
    color: #fff;
    padding: 4px 8px 4px;
    text-shadow: 0 1px 0 #2B587A;
    cursor: pointer;
    text-decoration: none;
}
.navigation_footer .link:hover {

    border-top: 1px solid #CAD1D9;
    color: #2B587A;
    background: #DAE1E8;
}
.navigation_footer .link {
    color: #2B587A;
}
.button:hover {
    color: #DAE1E8;
    text-decoration: none;
}
#activetabs a:hover {
    color: #DAE1E8;
    text-decoration: none;
}
.tabs a:hover {
    color: #346991;
    text-decoration: none;
}
#act_tab_a:hover {
    color: #346991;
    text-decoration: none;
}
.post-author {
    background-color: #f0f0f0;
    border-top: #45688e solid 1px;
    border-bottom: #ECECEC solid 1px;
    font-size: 11px;
    padding: 3px 5px 3px;
}
}
