css:
@-moz-document domain(casinotopsonline.com) {
.module-spotlights.v2 {
    padding: 2.4rem 0 3.2rem;
}
.module-spotlights.v2 .module-title {
    padding-bottom: 0;
}
.module-spotlights .spotlights-items.v2.mode-image_text .spotlights-item:first-of-type, .module-spotlights .spotlights-items.v2.mode-image_text .spotlights-item+.spotlights-item {
	border-radius: 3px;
	overflow: hidden;
}
.spotlights-item {
	transition: box-shadow 0.3s ease-in-out, background-color 0.3s ease-in-out;
}
.spotlights-item:hover {
	box-shadow: 0 0 56px 0 rgb(0 0 0 / 12%);
}
.module-spotlights .spotlights-items.v2.mode-image_text .spotlights-item .spotlight-item-content .spotlight-item-title {
	font-size: 1.6rem;
	line-height: 1.9rem;
	color: #383838;
	font-weight: 500;
}
.module-spotlights .spotlights-items.mode-image_text .spotlights-item a .spotlight-item-content .read-more-text p:after {
	content:"î ’";
	font-family: Fontello;
	color: hsl(240deg 18% 21%);
	font-size: 1.4rem;
	padding-left: .2rem;
	margin-top: .2rem;
	margin-left: 0;
	transition: color 0.2s ease, margin-left 0.2s ease;
}
.module-spotlights .spotlights-items.v2.mode-image_text .spotlights-item .spotlight-item-content .read-more-text p {
	white-space: nowrap;
	line-height: 1.6rem;
	color:  hsl(240deg 18% 36%);
	font-size: 1.3rem;
	font-weight: 600;
	transition: color 0.2s ease;
}
.module-spotlights .spotlights-items.v2.mode-image_text .spotlights-item .spotlight-item-content .read-more-text:hover > p, .module-spotlights .spotlights-items.v2.mode-image_text .spotlights-item .spotlight-item-content .read-more-text:hover > p:after {
	color: hsl(240deg 9% 31%);
}
.module-spotlights .spotlights-items.v2.mode-image_text .spotlights-item .spotlight-item-content .read-more-text:hover > p:after {
	margin-left:4px;
}
.module-spotlights .spotlights-items.mode-image_text .spotlights-item a .spotlight-item-content, .module-spotlights .spotlights-items.v2.mode-image_text .spotlights-item:first-of-type a .spotlight-item-content, .module-spotlights .spotlights-items.mode-image_text .spotlights-item a .spotlight-item-content {
	padding: 0 1.6rem 1.6rem;
	padding-top: 2.6rem;
}
.module-spotlights.v2, .module-spotlights .spotlights-items.v2.mode-image_text {
	background-color: hsl(240deg 30% 96%);
}
.module-spotlights .spotlights-items.v2.mode-image_text .spotlights-item .spotlight-item-label {
	position: absolute;
	top: unset;
	right: unset;
	max-height: 2.4rem;
	left: 1.6rem;
	bottom: -1.1rem;
}
.module-spotlights .spotlights-items.v2.mode-image_text .spotlights-item .spotlight-item-label p {
	background-color: hsl(240deg 26% 46%);
	color: #fff;
	padding: 0 0.8rem;
	border-radius: 0.2rem;
	line-height: 2.4rem;
	font-size: 1.1rem;
	font-weight: 600;
	height: 24px;
}
.module-spotlights .spotlights-items.v2.mode-image_text {
	grid-template-columns: 52rem 1fr 1fr;
	grid-template-rows: repeat(2, 1fr);
	grid-gap: 1.6rem;
}
}
