css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("justthetalk.com")
{

.container {
    font-family: Verdana;
    font-size: 100%
}
#logorow {
    display: block;
}
ol {
    background: white;
}
body {
	color: white;
	background: #202020;
}
a:link {
    color: white;
}
#folders li a:visited {
    decoration: none;
    color: lightgray;
}
a:visited, a:hover, a:active {
    color: white;
}
.discussionlistitem a {
    color: #5a91c4;
}
p {
    color: white;
}
.threadouterheader, .threadtop {
    background: #202020;
    
}
.breadcrumb {
    background: #202020;
}

.headerTextLink {
    color: #2A5880;
}
.discussionlistitem > div {
    color: white;
    font-size: 100%;
}
.well{
    border: 1px solid gray;
    background: #303030;
}
.bold a:link {
	color: #5a91c4;
}
textarea {
    background: #F5F5F5;
}
h4 {
    font-size: 100% !important;
}
.btn {
    color: black !important;
}

}
