css:
@-moz-document domain("heise.de") {
/* #################### General Body #################### */
body.a-theme{
    background-color: #1F2933;
    color:white;
}

/*
.a-theme{
	background-color: #102A43;
}
*/

svg {
    filter: invert(100%) sepia(3%) saturate(543%) hue-rotate(189deg) brightness(116%) contrast(100%);   
}

input{
 color:white;   
}

article{
	background-color: #323F4B;
	padding: 10px;
	color: white;
}
}

@-moz-document domain("heise.de") {
/* #################### HEAD Navigation #################### */

div.topnavigation{
	background-color: #243B53;
    color: white;
}

/*
div.topnavigation .topnavigation-login__icon{
	background-color: #243B53;
    color: white;
    filter: invert(99%) sepia(3%) saturate(543%) hue-rotate(189deg) brightness(116%) contrast(100%);
}
*/

div.topnavigation *{
	background-color: #243B53;
    color: white;
}

div.topnavigation .a-login__link{
    color: white;
}

/*
    filter: invert(99%) sepia(3%) saturate(543%) hue-rotate(189deg) brightness(116%) contrast(100%);*/
}

@-moz-document domain("heise.de") {
/* #################### News Category Navigation #################### */

[aria-label="heiseonline-Navigation"] a.navigation__head {
 	color:white;   
}

.top-topics__label{
    color:white;
}

.link.navigation__item{
	color:white;
}
}

@-moz-document domain("heise.de") {
/* Articel Grind */
.teaser-grid article{
    background-color: #323F4B;
	padding:10px;
}

.teaser-grid article footer{
	color:white;
}

.teaser-grid a.heiseplus-lnk{
	color:white;
}

.teaser-grid .a-article-teaser__meta{
	color:White;
}
}

@-moz-document domain("heise.de") {
/* #################### News selection #################### */

.a-layout{
 	background-color: #3E4C59;   
    color: white;
}

/* Articel Title */
.a-article-teaser__title-text{
    color: white;
    font-weight: bold
}

.a-article-teaser__synopsis{
    color:white;
}

.a-article-teaser__link footer{
	color:white;
}
}

@-moz-document domain("heise.de") {
/*Sidebar*/

.a-layout__sidebar *{
	color:white;
}
}

@-moz-document domain("heise.de") {
/* Stages */
.stage *{
	color:white;
}
}

@-moz-document domain("heise.de") {
/* Paging */

#pagination-heading{
	color:white;
}

.a-pagination__items .a-pagination__item .a-pagination__link{
	color:white;
}
}

@-moz-document domain("heise.de") {
/* News Roll */

.newsroll-message .newsroll-massage__link{
	color:white;
}

.module .module__content .teaser-list__title{
	color:white;	
}

.module .module__content footer{
	color:white;	
}
}

@-moz-document domain("heise.de") {
/* Shadow Module */
.ho-modules-container .shadow-modules{
 	background-color: #3E4C59;   
    color: white;
}

.ho-modules-container .shadow-modules *{
    color: white;
}
}

@-moz-document domain("heise.de") {
/* Bottom Nav */
.nav-bottom .keyword-alphabet{
	background-color: #3E4C59;
	color:white;
}

.nav-bottom .sitemap-group a{
	color:white;
}

.nav-bottom .sitemap-group .sitemap-group__header{
	color:white;
}

.nav-bottom .keyword-alphabet__link{
	background-color: #323F4B;
}
}

@-moz-document domain("heise.de") {
/* Footer */
footer.footer{
	background-color: #3E4C59;
	color:white;
}

footer.footer .footer__link{
	color:white;
}
}

@-moz-document url-prefix("https://www.heise.de/news/") {
/* Nachricht Viewer */
#meldung *{
	color:white;
}

#meldung .article-layout__content-container{
	color:white;
    font-weight: bold
}

.a-breadcrumb .a-breadcrumb__link{
	color:white;
}

#meldung .a-button[name*=".zurstartseite"]{
	background-color: #616E7C;
}

#meldung .topics__item .a-tag{
	background-color: #616E7C;
}
}
